  1→"use client";
  2→
  3→import { createContext, useContext, useEffect, useState } from "react";
  4→import { createClient } from "@/lib/supabase/client";
  5→import type { User } from "@supabase/supabase-js";
  6→
  7→interface Profile {
  8→  id: string;
  9→  email: string;
 10→  role: "guest" | "user" | "admin";
 11→  created_at: string;
 12→}
 13→
 14→interface AuthContextType {
 15→  user: User | null;
 16→  profile: Profile | null;
 17→  isAdmin: boolean;
 18→  isLoading: boolean;
 19→  signOut: () => Promise<void>;
 20→}
 21→
 22→const AuthContext = createContext<AuthContextType>({
 23→  user: null,
 24→  profile: null,
 25→  isAdmin: false,
 26→  isLoading: true,
 27→  signOut: async () => {},
 28→});
 29→
 30→export function AuthProvider({ children }: { children: React.ReactNode }) {
 31→  const [user, setUser] = useState<User | null>(null);
 32→  const [profile, setProfile] = useState<Profile | null>(null);
 33→  const [isLoading, setIsLoading] = useState(true);
 34→  const supabase = createClient();
 35→
 36→  useEffect(() => {
 37→    // Get initial session
 38→    const getSession = async () => {
 39→      const {
 40→        data: { user },
 41→      } = await supabase.auth.getUser();
 42→      setUser(user);
 43→      if (user) {
 44→        await fetchProfile(user.id);
 45→      }
 46→      setIsLoading(false);
 47→    };
 48→
 49→    getSession();
 50→
 51→    // Listen for auth changes
 52→    const {
 53→      data: { subscription },
 54→    } = supabase.auth.onAuthStateChange(async (_event, session) => {
 55→      const currentUser = session?.user ?? null;
 56→      setUser(currentUser);
 57→      if (currentUser) {
 58→        await fetchProfile(currentUser.id);
 59→      } else {
 60→        setProfile(null);
 61→      }
 62→      setIsLoading(false);
 63→    });
 64→
 65→    return () => subscription.unsubscribe();
 66→  }, []);
 67→
 68→  const fetchProfile = async (userId: string) => {
 69→    const { data, error } = await supabase
 70→      .from("profiles")
 71→      .select("*")
 72→      .eq("id", userId)
 73→      .single();
 74→    if (error || !data) {
 75→      console.error("Failed to fetch profile:", error?.message);
 76→      setProfile(null);
 77→      return;
 78→    }
 79→    setProfile(data);
 80→  };
 81→
 82→  const signOut = async () => {
 83→    await supabase.auth.signOut();
 84→    setUser(null);
 85→    setProfile(null);
 86→  };
 87→
 88→  return (
 89→    <AuthContext.Provider
 90→      value={{
 91→        user,
 92→        profile,
 93→        isAdmin: profile?.role === "admin",
 94→        isLoading,
 95→        signOut,
 96→      }}
 97→    >
 98→      {children}
 99→    </AuthContext.Provider>
100→  );
101→}
102→
103→export const useAuth = () => useContext(AuthContext);
104→

 1→import { createServerClient } from "@supabase/ssr";
 2→import { NextResponse, type NextRequest } from "next/server";
 3→
 4→export async function updateSession(request: NextRequest) {
 5→  let supabaseResponse = NextResponse.next({
 6→    request,
 7→  });
 8→
 9→  const supabase = createServerClient(
10→    process.env.NEXT_PUBLIC_SUPABASE_URL!,
11→    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
12→    {
13→      cookies: {
14→        getAll() {
15→          return request.cookies.getAll();
16→        },
17→        setAll(cookiesToSet) {
18→          cookiesToSet.forEach(({ name, value }) =>
19→            request.cookies.set(name, value)
20→          );
21→          supabaseResponse = NextResponse.next({
22→            request,
23→          });
24→          cookiesToSet.forEach(({ name, value, options }) =>
25→            supabaseResponse.cookies.set(name, value, options)
26→          );
27→        },
28→      },
29→    }
30→  );
31→
32→  // Refreshing the auth token
33→  await supabase.auth.getUser();
34→
35→  return supabaseResponse;
36→}
