  1→"use client";
  2→
  3→import { useState, useCallback } from "react";
  4→import { createClient } from "@/lib/supabase/client";
  5→import { StatusBadge } from "@/components/status-badge";
  6→import { HistoryEmpty } from "@/components/history-empty";
  7→import { useRealtimeTrackings } from "@/lib/use-realtime-trackings";
  8→import { useAuth } from "@/components/auth-provider";
  9→import type { Parcel, TrackingStatus } from "@/types";
 10→import { toast } from "sonner";
 11→
 12→const STATUS_OPTIONS: { value: string; label: string }[] = [
 13→  { value: "all", label: "All" },
 14→  { value: "transit", label: "In Transit" },
 15→  { value: "delivered", label: "Delivered" },
 16→  { value: "pending", label: "Pending" },
 17→  { value: "pickup", label: "Picked Up" },
 18→  { value: "exception", label: "Exception" },
 19→  { value: "expired", label: "Expired" },
 20→  { value: "notfound", label: "Not Found" },
 21→];
 22→
 23→type SortOrder = "newest" | "oldest";
 24→
 25→interface HistoryListProps {
 26→  initialTrackings: Parcel[];
 27→}
 28→
 29→export function HistoryList({ initialTrackings }: HistoryListProps) {
 30→  const [trackings, setTrackings] = useState<Parcel[]>(initialTrackings);
 31→  const [statusFilter, setStatusFilter] = useState("all");
 32→  const [sortOrder, setSortOrder] = useState<SortOrder>("newest");
 33→  const [selected, setSelected] = useState<Set<string>>(new Set());
 34→  const [refreshingIds, setRefreshingIds] = useState<Set<string>>(new Set());
 35→  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
 36→  const supabase = createClient();
 37→  const { user } = useAuth();
 38→
 39→  // Realtime subscriptions — live updates across tabs/devices
 40→  useRealtimeTrackings({
 41→    userId: user?.id,
 42→    onInsert: useCallback((parcel: Parcel) => {
 43→      setTrackings((prev) => {
 44→        if (prev.some((t) => t.id === parcel.id)) return prev;
 45→        return [parcel, ...prev];
 46→      });
 47→      toast.info(`New tracking added: ${parcel.tracking_number}`);
 48→    }, []),
 49→    onUpdate: useCallback((parcel: Parcel) => {
 50→      setTrackings((prev) =>
 51→        prev.map((t) => (t.id === parcel.id ? parcel : t))
 52→      );
 53→    }, []),
 54→    onDelete: useCallback((id: string) => {
 55→      setTrackings((prev) => prev.filter((t) => t.id !== id));
 56→      setSelected((prev) => {
 57→        const next = new Set(prev);
 58→        next.delete(id);
 59→        return next;
 60→      });
 61→    }, []),
 62→  });
 63→
 64→  // Filter
 65→  const filtered = trackings.filter(
 66→    (t) => statusFilter === "all" || t.status === statusFilter
 67→  );
 68→
 69→  // Sort
 70→  const sorted = [...filtered].sort((a, b) => {
 71→    const dateA = new Date(a.updated_at).getTime();
 72→    const dateB = new Date(b.updated_at).getTime();
 73→    return sortOrder === "newest" ? dateB - dateA : dateA - dateB;
 74→  });
 75→
 76→  // Selection
 77→  const toggleSelect = (id: string) => {
 78→    setSelected((prev) => {
 79→      const next = new Set(prev);
 80→      if (next.has(id)) next.delete(id);
 81→      else next.add(id);
 82→      return next;
 83→    });
 84→  };
 85→
 86→  const toggleSelectAll = () => {
 87→    if (selected.size === sorted.length) {
 88→      setSelected(new Set());
 89→    } else {
 90→      setSelected(new Set(sorted.map((t) => t.id)));
 91→    }
 92→  };
 93→
 94→  // Refresh a single tracking
 95→  const refreshTracking = useCallback(
 96→    async (tracking: Parcel) => {
 97→      setRefreshingIds((prev) => new Set(prev).add(tracking.id));
 98→      try {
 99→        const res = await fetch("/api/track", {
100→          method: "POST",
101→          headers: { "Content-Type": "application/json" },
102→          body: JSON.stringify({
103→            trackingNumber: tracking.tracking_number,
104→            courierCode: tracking.courier_code,
105→          }),
106→        });
107→        const json = await res.json();
108→        if (json.data) {
109→          // Update local state
110→          setTrackings((prev) =>
111→            prev.map((t) =>
112→              t.id === tracking.id
113→                ? {
114→                    ...t,
115→                    status: json.data.status,
116→                    last_event: json.data.last_event || t.last_event,
117→                    checkpoints: json.data.checkpoints || t.checkpoints,
118→                    updated_at: new Date().toISOString(),
119→                  }
120→                : t
121→            )
122→          );
123→          toast.success(`Updated ${tracking.tracking_number}`);
124→        } else {
125→          toast.error(json.error || "Failed to refresh");
126→        }
127→      } catch {
128→        toast.error("Failed to refresh tracking");
129→      } finally {
130→        setRefreshingIds((prev) => {
131→          const next = new Set(prev);
132→          next.delete(tracking.id);
133→          return next;
134→        });
135→      }
136→    },
137→    []
138→  );
139→
140→  // Toggle public/private
141→  const togglePublic = async (tracking: Parcel) => {
142→    const newValue = !tracking.is_public;
143→    const { error } = await supabase
144→      .from("trackings")
145→      .update({ is_public: newValue })
146→      .eq("id", tracking.id);
147→    if (error) {
148→      toast.error("Failed to update visibility");
149→      return;
150→    }
151→    setTrackings((prev) =>
152→      prev.map((t) =>
153→        t.id === tracking.id ? { ...t, is_public: newValue } : t
154→      )
155→    );
156→    if (newValue && tracking.public_slug) {
157→      const url = `${window.location.origin}/track/${tracking.public_slug}`;
158→      await navigator.clipboard.writeText(url);
159→      toast.success("Link copied! Tracking is now public.");
160→    } else {
161→      toast.success("Tracking is now private.");
162→    }
163→  };
164→
165→  // Delete single
166→  const deleteTracking = async (id: string) => {
167→    const { error } = await supabase.from("trackings").delete().eq("id", id);
168→    if (error) {
169→      toast.error("Failed to delete");
170→      return;
171→    }
172→    setTrackings((prev) => prev.filter((t) => t.id !== id));
173→    setSelected((prev) => {
174→      const next = new Set(prev);
175→      next.delete(id);
176→      return next;
177→    });
178→    toast.success("Tracking deleted");
179→  };
180→
181→  // Bulk delete
182→  const bulkDelete = async () => {
183→    const ids = Array.from(selected);
184→    const { error } = await supabase
185→      .from("trackings")
186→      .delete()
187→      .in("id", ids);
188→    if (error) {
189→      toast.error("Failed to delete trackings");
190→      return;
191→    }
192→    setTrackings((prev) => prev.filter((t) => !selected.has(t.id)));
193→    setSelected(new Set());
194→    setShowDeleteConfirm(false);
195→    toast.success(`${ids.length} tracking(s) deleted`);
196→  };
197→
198→  if (!trackings.length) {
199→    return <HistoryEmpty />;
200→  }
201→
202→  return (
203→    <div className="space-y-4">
204→      {/* Toolbar */}
205→      <div className="flex flex-wrap items-center gap-3">
206→        {/* Status filter */}
207→        <select
208→          value={statusFilter}
209→          onChange={(e) => setStatusFilter(e.target.value)}
210→          className="rounded-md border border-zinc-300 px-3 py-1.5 text-sm dark:border-zinc-700 dark:bg-zinc-900 dark:text-white"
211→        >
212→          {STATUS_OPTIONS.map((opt) => (
213→            <option key={opt.value} value={opt.value}>
214→              {opt.label}
215→            </option>
216→          ))}
217→        </select>
218→
219→        {/* Sort */}
220→        <select
221→          value={sortOrder}
222→          onChange={(e) => setSortOrder(e.target.value as SortOrder)}
223→          className="rounded-md border border-zinc-300 px-3 py-1.5 text-sm dark:border-zinc-700 dark:bg-zinc-900 dark:text-white"
224→        >
225→          <option value="newest">Newest first</option>
226→          <option value="oldest">Oldest first</option>
227→        </select>
228→
229→        {/* Bulk actions */}
230→        {selected.size > 0 && (
231→          <div className="ml-auto flex items-center gap-2">
232→            <span className="text-sm text-zinc-500">
233→              {selected.size} selected
234→            </span>
235→            <button
236→              onClick={() => setShowDeleteConfirm(true)}
237→              className="rounded-md bg-red-600 px-3 py-1.5 text-sm font-medium text-white hover:bg-red-700"
238→            >
239→              Delete selected
240→            </button>
241→          </div>
242→        )}
243→      </div>
244→
245→      {/* Delete confirmation modal */}
246→      {showDeleteConfirm && (
247→        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50">
248→          <div className="mx-4 w-full max-w-sm rounded-lg bg-white p-6 shadow-xl dark:bg-zinc-900">
249→            <h3 className="text-lg font-semibold">Confirm Delete</h3>
250→            <p className="mt-2 text-sm text-zinc-500 dark:text-zinc-400">
251→              Are you sure you want to delete {selected.size} tracking(s)? This
252→              action cannot be undone.
253→            </p>
254→            <div className="mt-4 flex justify-end gap-2">
255→              <button
256→                onClick={() => setShowDeleteConfirm(false)}
257→                className="rounded-md px-4 py-2 text-sm font-medium text-zinc-600 hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-800"
258→              >
259→                Cancel
260→              </button>
261→              <button
262→                onClick={bulkDelete}
263→                className="rounded-md bg-red-600 px-4 py-2 text-sm font-medium text-white hover:bg-red-700"
264→              >
265→                Delete
266→              </button>
267→            </div>
268→          </div>
269→        </div>
270→      )}
271→
272→      {/* Table header */}
273→      <div className="hidden items-center gap-4 border-b border-zinc-200 px-4 py-2 text-xs font-medium uppercase text-zinc-500 sm:flex dark:border-zinc-800">
274→        <input
275→          type="checkbox"
276→          checked={selected.size === sorted.length && sorted.length > 0}
277→          onChange={toggleSelectAll}
278→          className="h-4 w-4 rounded border-zinc-300"
279→        />
280→        <span className="flex-1">Tracking Number</span>
281→        <span className="w-28">Courier</span>
282→        <span className="w-24">Status</span>
283→        <span className="w-32">Last Updated</span>
284→        <span className="w-24">Actions</span>
285→      </div>
286→
287→      {/* Tracking rows */}
288→      <div className="space-y-2">
289→        {sorted.map((tracking) => (
290→          <div
291→            key={tracking.id}
292→            className="flex flex-col gap-3 rounded-lg border border-zinc-200 px-4 py-3 sm:flex-row sm:items-center sm:gap-4 dark:border-zinc-800"
293→          >
294→            {/* Checkbox */}
295→            <input
296→              type="checkbox"
297→              checked={selected.has(tracking.id)}
298→              onChange={() => toggleSelect(tracking.id)}
299→              className="h-4 w-4 shrink-0 rounded border-zinc-300"
300→            />
301→
302→            {/* Tracking number + last event */}
303→            <div className="min-w-0 flex-1">
304→              <p className="truncate font-mono text-sm font-medium">
305→                {tracking.tracking_number}
306→              </p>
307→              {tracking.last_event && (
308→                <p className="truncate text-xs text-zinc-500 dark:text-zinc-400">
309→                  {tracking.last_event}
310→                </p>
311→              )}
312→            </div>
313→
314→            {/* Courier */}
315→            <span className="w-28 text-sm text-zinc-600 dark:text-zinc-400">
316→              {tracking.courier_code}
317→            </span>
318→
319→            {/* Status */}
320→            <div className="w-24">
321→              <StatusBadge status={tracking.status as TrackingStatus} />
322→            </div>
323→
324→            {/* Date */}
325→            <span className="w-32 text-xs text-zinc-500 dark:text-zinc-400">
326→              {new Date(tracking.updated_at).toLocaleDateString("en-US", {
327→                month: "short",
328→                day: "numeric",
329→                hour: "2-digit",
330→                minute: "2-digit",
331→              })}
332→            </span>
333→
334→            {/* Actions */}
