351→                  stroke="currentColor"
352→                  strokeWidth="2"
353→                  strokeLinecap="round"
354→                  strokeLinejoin="round"
355→                >
356→                  {tracking.is_public ? (
357→                    <>
358→                      <circle cx="18" cy="5" r="3" />
359→                      <circle cx="6" cy="12" r="3" />
360→                      <circle cx="18" cy="19" r="3" />
361→                      <line x1="8.59" y1="13.51" x2="15.42" y2="17.49" />
362→                      <line x1="15.41" y1="6.51" x2="8.59" y2="10.49" />
363→                    </>
364→                  ) : (
365→                    <>
366→                      <path d="M4 12v8a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2v-8" />
367→                      <polyline points="16 6 12 2 8 6" />
368→                      <line x1="12" y1="2" x2="12" y2="15" />
369→                    </>
