  1→"use client";
  2→
  3→import { useState, useCallback, useRef } from "react";
  4→import Papa from "papaparse";
  5→
  6→type Step = "upload" | "review" | "processing" | "summary";
  7→
  8→interface CsvRow {
  9→  id: string;
 10→  trackingNumber: string;
 11→  courierCode: string;
 12→  label: string;
 13→  valid: boolean;
 14→  error?: string;
 15→}
 16→
 17→interface ImportResult {
 18→  trackingNumber: string;
 19→  success: boolean;
 20→  error?: string;
 21→  status?: string;
 22→}
 23→
 24→const BATCH_SIZE = 3; // Process 3 at a time to avoid rate limits
 25→
 26→export function ImportWizard() {
 27→  const [step, setStep] = useState<Step>("upload");
 28→  const [rows, setRows] = useState<CsvRow[]>([]);
 29→  const [results, setResults] = useState<ImportResult[]>([]);
 30→  const [progress, setProgress] = useState(0);
 31→  const [totalToProcess, setTotalToProcess] = useState(0);
 32→  const [isDragOver, setIsDragOver] = useState(false);
 33→  const fileInputRef = useRef<HTMLInputElement>(null);
 34→
 35→  // Parse CSV
 36→  const parseFile = useCallback((file: File) => {
 37→    Papa.parse(file, {
 38→      header: true,
 39→      skipEmptyLines: true,
 40→      transformHeader: (h: string) => h.trim().toLowerCase().replace(/\s+/g, "_"),
 41→      complete: (result) => {
 42→        const parsed: CsvRow[] = (result.data as Record<string, string>[]).map(
 43→          (row, i) => {
 44→            const trackingNumber = (
 45→              row.tracking_number ||
 46→              row.trackingnumber ||
 47→              row.tracking ||
 48→              row.number ||
 49→              ""
 50→            ).trim();
 51→            const courierCode = (
 52→              row.courier_code ||
 53→              row.couriercode ||
 54→              row.courier ||
 55→              row.carrier ||
 56→              ""
 57→            ).trim();
 58→            const label = (row.label || row.name || row.description || "").trim();
 59→
 60→            let valid = true;
 61→            let error: string | undefined;
 62→
 63→            if (!trackingNumber) {
 64→              valid = false;
 65→              error = "Missing tracking number";
 66→            } else if (!courierCode) {
 67→              valid = false;
 68→              error = "Missing courier code";
 69→            }
 70→
 71→            return {
 72→              id: `row-${i}`,
 73→              trackingNumber,
 74→              courierCode,
 75→              label,
 76→              valid,
 77→              error,
 78→            };
 79→          }
 80→        );
 81→
 82→        setRows(parsed);
 83→        setStep("review");
 84→      },
 85→      error: () => {
 86→        setRows([]);
 87→      },
 88→    });
 89→  }, []);
 90→
 91→  const handleDrop = useCallback(
 92→    (e: React.DragEvent) => {
 93→      e.preventDefault();
 94→      setIsDragOver(false);
 95→      const file = e.dataTransfer.files[0];
 96→      if (file && file.name.endsWith(".csv")) {
 97→        parseFile(file);
 98→      }
 99→    },
100→    [parseFile]
101→  );
102→
103→  const handleFileChange = useCallback(
104→    (e: React.ChangeEvent<HTMLInputElement>) => {
105→      const file = e.target.files?.[0];
106→      if (file) parseFile(file);
107→    },
108→    [parseFile]
109→  );
110→
111→  const removeRow = (id: string) => {
112→    setRows((prev) => prev.filter((r) => r.id !== id));
113→  };
114→
115→  const updateRow = (id: string, field: keyof CsvRow, value: string) => {
116→    setRows((prev) =>
117→      prev.map((r) => {
118→        if (r.id !== id) return r;
119→        const updated = { ...r, [field]: value };
120→        // Revalidate
121→        if (updated.trackingNumber && updated.courierCode) {
122→          updated.valid = true;
123→          updated.error = undefined;
124→        } else {
125→          updated.valid = false;
126→          updated.error = !updated.trackingNumber
127→            ? "Missing tracking number"
128→            : "Missing courier code";
129→        }
130→        return updated;
131→      })
132→    );
133→  };
134→
135→  // Process in batches
136→  const processImport = async () => {
137→    const validRows = rows.filter((r) => r.valid);
138→    setTotalToProcess(validRows.length);
139→    setProgress(0);
140→    setResults([]);
141→    setStep("processing");
142→
143→    const allResults: ImportResult[] = [];
144→
145→    for (let i = 0; i < validRows.length; i += BATCH_SIZE) {
146→      const batch = validRows.slice(i, i + BATCH_SIZE);
147→
148→      const batchResults = await Promise.all(
149→        batch.map(async (row) => {
150→          try {
151→            const res = await fetch("/api/import", {
152→              method: "POST",
153→              headers: { "Content-Type": "application/json" },
154→              body: JSON.stringify({
155→                trackingNumber: row.trackingNumber,
156→                courierCode: row.courierCode,
157→                label: row.label,
158→              }),
159→            });
160→            const json = await res.json();
161→            return {
162→              trackingNumber: row.trackingNumber,
163→              success: json.success ?? false,
164→              error: json.error,
165→              status: json.status,
166→            };
167→          } catch {
168→            return {
169→              trackingNumber: row.trackingNumber,
170→              success: false,
171→              error: "Request failed",
172→            };
173→          }
174→        })
175→      );
176→
177→      allResults.push(...batchResults);
178→      setResults([...allResults]);
179→      setProgress(Math.min(allResults.length, validRows.length));
180→    }
181→
182→    setStep("summary");
183→  };
184→
185→  const validCount = rows.filter((r) => r.valid).length;
186→  const invalidCount = rows.filter((r) => !r.valid).length;
187→
188→  return (
189→    <div className="space-y-6">
190→      {/* Step: Upload */}
191→      {step === "upload" && (
192→        <div>
193→          <div
194→            onDragOver={(e) => {
195→              e.preventDefault();
196→              setIsDragOver(true);
197→            }}
198→            onDragLeave={() => setIsDragOver(false)}
199→            onDrop={handleDrop}
200→            onClick={() => fileInputRef.current?.click()}
201→            className={`flex cursor-pointer flex-col items-center justify-center rounded-lg border-2 border-dashed py-16 transition-colors ${
202→              isDragOver
203→                ? "border-zinc-500 bg-zinc-50 dark:border-zinc-400 dark:bg-zinc-900"
204→                : "border-zinc-300 hover:border-zinc-400 dark:border-zinc-700 dark:hover:border-zinc-600"
205→            }`}
206→          >
207→            <svg
208→              xmlns="http://www.w3.org/2000/svg"
209→              width="40"
210→              height="40"
211→              viewBox="0 0 24 24"
212→              fill="none"
213→              stroke="currentColor"
214→              strokeWidth="1.5"
215→              strokeLinecap="round"
216→              strokeLinejoin="round"
217→              className="text-zinc-400"
218→            >
219→              <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
220→              <polyline points="17 8 12 3 7 8" />
221→              <line x1="12" y1="3" x2="12" y2="15" />
222→            </svg>
223→            <p className="mt-4 text-sm font-medium">
224→              Drop your CSV file here or click to browse
225→            </p>
226→            <p className="mt-1 text-xs text-zinc-500 dark:text-zinc-400">
227→              Required columns: tracking_number, courier_code. Optional: label
228→            </p>
229→            <input
230→              ref={fileInputRef}
231→              type="file"
232→              accept=".csv"
233→              onChange={handleFileChange}
234→              className="hidden"
235→            />
236→          </div>
237→
238→          <div className="mt-4 rounded-lg border border-zinc-200 p-4 dark:border-zinc-800">
239→            <h3 className="text-sm font-medium">CSV Format Example</h3>
240→            <pre className="mt-2 overflow-x-auto text-xs text-zinc-500 dark:text-zinc-400">
241→{`tracking_number,courier_code,label
242→EA152563254CN,china-ems,My Package
243→9400111899223456789012,usps,Gift for Mom
244→JD0012345678901234,ups,Office Supplies`}
245→            </pre>
246→          </div>
247→        </div>
248→      )}
249→
250→      {/* Step: Review */}
251→      {step === "review" && (
252→        <div>
253→          <div className="mb-4 flex items-center justify-between">
254→            <div className="flex items-center gap-3">
255→              <span className="text-sm">
256→                <strong>{validCount}</strong> valid,{" "}
257→                {invalidCount > 0 && (
258→                  <span className="text-red-600 dark:text-red-400">
259→                    <strong>{invalidCount}</strong> invalid
260→                  </span>
261→                )}
262→                {invalidCount === 0 && (
263→                  <span className="text-green-600 dark:text-green-400">
264→                    0 invalid
265→                  </span>
266→                )}
267→              </span>
268→            </div>
269→            <div className="flex gap-2">
270→              <button
271→                onClick={() => {
272→                  setStep("upload");
273→                  setRows([]);
274→                }}
275→                className="rounded-md px-3 py-1.5 text-sm text-zinc-600 hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-800"
276→              >
277→                Re-upload
278→              </button>
279→              <button
280→                onClick={processImport}
281→                disabled={validCount === 0}
282→                className="rounded-md bg-zinc-900 px-4 py-1.5 text-sm font-medium text-white hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
283→              >
284→                Import {validCount} tracking(s)
285→              </button>
286→            </div>
287→          </div>
288→
289→          <div className="overflow-x-auto rounded-lg border border-zinc-200 dark:border-zinc-800">
290→            <table className="w-full text-sm">
291→              <thead>
292→                <tr className="border-b border-zinc-200 bg-zinc-50 text-left text-xs text-zinc-500 dark:border-zinc-800 dark:bg-zinc-900">
293→                  <th className="px-3 py-2 font-medium">#</th>
294→                  <th className="px-3 py-2 font-medium">Tracking Number</th>
295→                  <th className="px-3 py-2 font-medium">Courier</th>
296→                  <th className="px-3 py-2 font-medium">Label</th>
297→                  <th className="px-3 py-2 font-medium">Status</th>
298→                  <th className="px-3 py-2 font-medium"></th>
299→                </tr>
300→              </thead>
301→              <tbody>
302→                {rows.map((row, i) => (
303→                  <tr
304→                    key={row.id}
305→                    className={`border-b border-zinc-100 dark:border-zinc-800/50 ${
306→                      !row.valid
307→                        ? "bg-red-50 dark:bg-red-950/20"
