309→                    }`}
310→                  >
311→                    <td className="px-3 py-2 text-xs text-zinc-400">
312→                      {i + 1}
313→                    </td>
314→                    <td className="px-3 py-2">
315→                      <input
316→                        value={row.trackingNumber}
317→                        onChange={(e) =>
318→                          updateRow(row.id, "trackingNumber", e.target.value)
319→                        }
320→                        className="w-full rounded border border-zinc-200 px-2 py-1 font-mono text-xs dark:border-zinc-700 dark:bg-zinc-900"
321→                      />
322→                    </td>
323→                    <td className="px-3 py-2">
324→                      <input
325→                        value={row.courierCode}
326→                        onChange={(e) =>
327→                          updateRow(row.id, "courierCode", e.target.value)
328→                        }
329→                        className="w-full rounded border border-zinc-200 px-2 py-1 text-xs dark:border-zinc-700 dark:bg-zinc-900"
330→                      />
331→                    </td>
332→                    <td className="px-3 py-2">
333→                      <input
334→                        value={row.label}
335→                        onChange={(e) =>
336→                          updateRow(row.id, "label", e.target.value)
337→                        }
338→                        className="w-full rounded border border-zinc-200 px-2 py-1 text-xs dark:border-zinc-700 dark:bg-zinc-900"
339→                      />
340→                    </td>
341→                    <td className="px-3 py-2">
342→                      {row.valid ? (
343→                        <span className="text-xs text-green-600 dark:text-green-400">
344→                          Valid
345→                        </span>
346→                      ) : (
347→                        <span className="text-xs text-red-600 dark:text-red-400">
348→                          {row.error}
349→                        </span>
350→                      )}
351→                    </td>
352→                    <td className="px-3 py-2">
353→                      <button
354→                        onClick={() => removeRow(row.id)}
355→                        className="text-zinc-400 hover:text-red-500"
356→                      >
357→                        <svg
358→                          xmlns="http://www.w3.org/2000/svg"
359→                          width="14"
360→                          height="14"
361→                          viewBox="0 0 24 24"
362→                          fill="none"
363→                          stroke="currentColor"
364→                          strokeWidth="2"
365→                        >
366→                          <line x1="18" y1="6" x2="6" y2="18" />
367→                          <line x1="6" y1="6" x2="18" y2="18" />
368→                        </svg>
369→                      </button>
370→                    </td>
371→                  </tr>
372→                ))}
373→              </tbody>
374→            </table>
375→          </div>
376→        </div>
377→      )}
378→
379→      {/* Step: Processing */}
380→      {step === "processing" && (
381→        <div className="space-y-4">
382→          <div className="text-center">
383→            <h2 className="text-lg font-semibold">Importing trackings...</h2>
384→            <p className="mt-1 text-sm text-zinc-500 dark:text-zinc-400">
385→              {progress} of {totalToProcess} processed
386→            </p>
387→          </div>
388→
389→          {/* Progress bar */}
390→          <div className="h-3 overflow-hidden rounded-full bg-zinc-200 dark:bg-zinc-800">
391→            <div
392→              className="h-full rounded-full bg-zinc-900 transition-all duration-300 dark:bg-white"
393→              style={{
394→                width: `${totalToProcess > 0 ? (progress / totalToProcess) * 100 : 0}%`,
395→              }}
396→            />
397→          </div>
398→
399→          {/* Live results */}
400→          <div className="max-h-64 space-y-1 overflow-y-auto">
401→            {results.map((r, i) => (
402→              <div
403→                key={i}
404→                className="flex items-center gap-2 rounded px-3 py-1.5 text-xs"
405→              >
406→                {r.success ? (
407→                  <span className="text-green-600 dark:text-green-400">
408→                    OK
409→                  </span>
410→                ) : (
411→                  <span className="text-red-600 dark:text-red-400">
412→                    FAIL
413→                  </span>
414→                )}
415→                <span className="font-mono">{r.trackingNumber}</span>
416→                {r.error && (
417→                  <span className="text-zinc-400">— {r.error}</span>
418→                )}
419→              </div>
420→            ))}
421→          </div>
422→        </div>
423→      )}
424→
425→      {/* Step: Summary */}
426→      {step === "summary" && (
427→        <div className="space-y-4">
428→          <div className="text-center">
429→            <h2 className="text-lg font-semibold">Import Complete</h2>
430→          </div>
431→
432→          <div className="grid grid-cols-2 gap-4">
433→            <div className="rounded-lg border border-green-200 bg-green-50 px-4 py-3 dark:border-green-800 dark:bg-green-950/30">
434→              <p className="text-xs text-green-600 dark:text-green-400">
435→                Succeeded
436→              </p>
437→              <p className="text-2xl font-bold text-green-700 dark:text-green-300">
438→                {results.filter((r) => r.success).length}
439→              </p>
440→            </div>
441→            <div className="rounded-lg border border-red-200 bg-red-50 px-4 py-3 dark:border-red-800 dark:bg-red-950/30">
442→              <p className="text-xs text-red-600 dark:text-red-400">Failed</p>
443→              <p className="text-2xl font-bold text-red-700 dark:text-red-300">
444→                {results.filter((r) => !r.success).length}
445→              </p>
446→            </div>
447→          </div>
448→
449→          {/* Failed details */}
450→          {results.some((r) => !r.success) && (
451→            <div className="rounded-lg border border-zinc-200 p-4 dark:border-zinc-800">
452→              <h3 className="mb-2 text-sm font-medium">Failed Items</h3>
453→              <div className="space-y-1">
454→                {results
455→                  .filter((r) => !r.success)
456→                  .map((r, i) => (
457→                    <div key={i} className="flex items-center gap-2 text-xs">
458→                      <span className="font-mono">{r.trackingNumber}</span>
459→                      <span className="text-red-500">{r.error}</span>
460→                    </div>
461→                  ))}
462→              </div>
463→            </div>
464→          )}
465→
466→          <div className="flex justify-center gap-3">
467→            <button
468→              onClick={() => {
469→                setStep("upload");
470→                setRows([]);
471→                setResults([]);
472→              }}
473→              className="rounded-md px-4 py-2 text-sm text-zinc-600 hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-800"
474→            >
475→              Import more
476→            </button>
477→            <a
478→              href="/history"
479→              className="rounded-md bg-zinc-900 px-4 py-2 text-sm font-medium text-white hover:bg-zinc-800 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
480→            >
481→              View history
482→            </a>
483→          </div>
484→        </div>
485→      )}
486→    </div>
487→  );
488→}
