  1→"use client";
  2→
  3→import { useState } from "react";
  4→import { toast } from "sonner";
  5→import { contactFormSchema } from "@/lib/validators";
  6→import type { ContactFormData } from "@/types";
  7→
  8→export default function ContactPage() {
  9→  const [form, setForm] = useState<ContactFormData>({
 10→    name: "",
 11→    email: "",
 12→    subject: "",
 13→    message: "",
 14→  });
 15→  const [errors, setErrors] = useState<Record<string, string>>({});
 16→  const [loading, setLoading] = useState(false);
 17→  const [submitted, setSubmitted] = useState(false);
 18→
 19→  function handleChange(
 20→    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
 21→  ) {
 22→    const { name, value } = e.target;
 23→    setForm((prev) => ({ ...prev, [name]: value }));
 24→    if (errors[name]) {
 25→      setErrors((prev) => {
 26→        const next = { ...prev };
 27→        delete next[name];
 28→        return next;
 29→      });
 30→    }
 31→  }
 32→
 33→  async function handleSubmit(e: React.FormEvent) {
 34→    e.preventDefault();
 35→    setErrors({});
 36→
 37→    // Client-side validation
 38→    const result = contactFormSchema.safeParse(form);
 39→    if (!result.success) {
 40→      const fieldErrors: Record<string, string> = {};
 41→      for (const issue of result.error.issues) {
 42→        const key = String(issue.path[0]);
 43→        if (!fieldErrors[key]) {
 44→          fieldErrors[key] = issue.message;
 45→        }
 46→      }
 47→      setErrors(fieldErrors);
 48→      return;
 49→    }
 50→
 51→    setLoading(true);
 52→
 53→    try {
 54→      const res = await fetch("/api/contact", {
 55→        method: "POST",
 56→        headers: { "Content-Type": "application/json" },
 57→        body: JSON.stringify(form),
 58→      });
 59→
 60→      const data = await res.json();
 61→
 62→      if (!res.ok) {
 63→        toast.error(data.error || "Something went wrong");
 64→        return;
 65→      }
 66→
 67→      toast.success("Message sent! We'll get back to you soon.");
 68→      setSubmitted(true);
 69→    } catch {
 70→      toast.error("Failed to send message. Please try again.");
 71→    } finally {
 72→      setLoading(false);
 73→    }
 74→  }
 75→
 76→  if (submitted) {
 77→    return (
 78→      <div className="mx-auto max-w-lg px-4 py-16 text-center">
 79→        <div className="mx-auto mb-4 flex h-16 w-16 items-center justify-center rounded-full bg-green-100 dark:bg-green-900/30">
 80→          <svg
 81→            className="h-8 w-8 text-green-600 dark:text-green-400"
 82→            fill="none"
 83→            viewBox="0 0 24 24"
 84→            strokeWidth={2}
 85→            stroke="currentColor"
 86→          >
 87→            <path
 88→              strokeLinecap="round"
 89→              strokeLinejoin="round"
 90→              d="M4.5 12.75l6 6 9-13.5"
 91→            />
 92→          </svg>
 93→        </div>
 94→        <h1 className="text-2xl font-bold tracking-tight">
 95→          Thank you!
 96→        </h1>
 97→        <p className="mt-2 text-zinc-500 dark:text-zinc-400">
 98→          Your message has been sent. We&apos;ll get back to you as soon as
 99→          possible.
100→        </p>
101→        <button
102→          onClick={() => {
103→            setSubmitted(false);
104→            setForm({ name: "", email: "", subject: "", message: "" });
105→          }}
106→          className="mt-6 rounded-lg bg-zinc-900 px-4 py-2.5 text-sm font-medium text-white hover:bg-zinc-800 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
107→        >
108→          Send another message
109→        </button>
110→      </div>
111→    );
112→  }
113→
114→  const inputClass =
115→    "mt-1 block w-full rounded-lg border border-zinc-300 px-3 py-2 text-sm shadow-sm focus:border-zinc-500 focus:outline-none focus:ring-1 focus:ring-zinc-500 dark:border-zinc-700 dark:bg-zinc-900 dark:text-white";
116→  const labelClass =
117→    "block text-sm font-medium text-zinc-700 dark:text-zinc-300";
118→
119→  return (
120→    <div className="mx-auto max-w-lg px-4 py-8">
121→      <div className="mb-8">
122→        <h1 className="text-2xl font-bold tracking-tight">Contact Us</h1>
123→        <p className="mt-1 text-sm text-zinc-500 dark:text-zinc-400">
124→          Have a question or feedback? Send us a message and we&apos;ll
125→          respond as soon as we can.
126→        </p>
127→      </div>
128→
129→      <form onSubmit={handleSubmit} className="space-y-4">
130→        <div>
131→          <label htmlFor="name" className={labelClass}>
132→            Name
133→          </label>
134→          <input
135→            id="name"
136→            name="name"
137→            type="text"
138→            value={form.name}
139→            onChange={handleChange}
140→            className={inputClass}
141→            placeholder="Your name"
142→          />
143→          {errors.name && (
144→            <p className="mt-1 text-xs text-red-500">{errors.name}</p>
145→          )}
146→        </div>
147→
148→        <div>
149→          <label htmlFor="email" className={labelClass}>
150→            Email
151→          </label>
152→          <input
153→            id="email"
154→            name="email"
155→            type="email"
156→            value={form.email}
157→            onChange={handleChange}
158→            className={inputClass}
159→            placeholder="you@example.com"
160→          />
161→          {errors.email && (
162→            <p className="mt-1 text-xs text-red-500">{errors.email}</p>
163→          )}
164→        </div>
165→
166→        <div>
167→          <label htmlFor="subject" className={labelClass}>
168→            Subject
169→          </label>
170→          <input
171→            id="subject"
172→            name="subject"
173→            type="text"
174→            value={form.subject}
175→            onChange={handleChange}
176→            className={inputClass}
177→            placeholder="What is this about?"
178→          />
179→          {errors.subject && (
180→            <p className="mt-1 text-xs text-red-500">{errors.subject}</p>
181→          )}
182→        </div>
183→
184→        <div>
185→          <label htmlFor="message" className={labelClass}>
186→            Message
187→          </label>
188→          <textarea
189→            id="message"
190→            name="message"
191→            rows={5}
192→            value={form.message}
193→            onChange={handleChange}
194→            className={inputClass + " resize-none"}
195→            placeholder="Your message (at least 10 characters)"
196→          />
197→          <div className="mt-1 flex items-center justify-between">
198→            {errors.message ? (
199→              <p className="text-xs text-red-500">{errors.message}</p>
200→            ) : (
201→              <span />
202→            )}
203→            <span className="text-xs text-zinc-400">
204→              {form.message.length}/2000
205→            </span>
206→          </div>
207→        </div>
208→
209→        <button
210→          type="submit"
211→          disabled={loading}
212→          className="w-full rounded-lg bg-zinc-900 px-4 py-2.5 text-sm font-medium text-white hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
213→        >
214→          {loading ? "Sending..." : "Send Message"}
215→        </button>
216→      </form>
217→    </div>
218→  );
219→}
