  1→"use client";
  2→
  3→import { useState, useCallback, useEffect } from "react";
  4→import { useRealtimeTrackings } from "@/lib/use-realtime-trackings";
  5→import { useAuth } from "@/components/auth-provider";
  6→import { StatusBadge } from "@/components/status-badge";
  7→import { VolumeChart } from "@/components/charts/volume-chart";
  8→import { CourierChart } from "@/components/charts/courier-chart";
  9→import Link from "next/link";
 10→import { toast } from "sonner";
 11→import type { Parcel, TrackingStatus } from "@/types";
 12→
 13→interface DashboardStats {
 14→  summary: {
 15→    total: number;
 16→    inTransit: number;
 17→    deliveredThisMonth: number;
 18→    avgDeliveryDays: number;
 19→  };
 20→  dailyVolume: { date: string; count: number }[];
 21→  courierBreakdown: {
 22→    courier: string;
 23→    total: number;
 24→    delivered: number;
 25→    successRate: number;
 26→  }[];
 27→  inTransitParcels: Parcel[];
 28→}
 29→
 30→interface DashboardClientProps {
 31→  initialTrackings: Parcel[];
 32→}
 33→
 34→export function DashboardClient({ initialTrackings }: DashboardClientProps) {
 35→  const { user } = useAuth();
 36→  const [stats, setStats] = useState<DashboardStats | null>(null);
 37→  const [refreshingIds, setRefreshingIds] = useState<Set<string>>(new Set());
 38→  const [, setTrackings] = useState<Parcel[]>(initialTrackings);
 39→
 40→  // Fetch server-side computed stats
 41→  const fetchStats = useCallback(async () => {
 42→    try {
 43→      const res = await fetch("/api/dashboard");
 44→      if (res.ok) {
 45→        const data = await res.json();
 46→        setStats(data);
 47→      }
 48→    } catch {
 49→      // silently fail
 50→    }
 51→  }, []);
 52→
 53→  useEffect(() => {
 54→    fetchStats();
 55→  }, [fetchStats]);
 56→
 57→  // Realtime — refetch stats when data changes
 58→  useRealtimeTrackings({
 59→    userId: user?.id,
 60→    onInsert: useCallback(
 61→      (parcel: Parcel) => {
 62→        setTrackings((prev) => {
 63→          if (prev.some((t) => t.id === parcel.id)) return prev;
 64→          return [parcel, ...prev];
 65→        });
 66→        fetchStats();
 67→      },
 68→      [fetchStats]
 69→    ),
 70→    onUpdate: useCallback(
 71→      (parcel: Parcel) => {
 72→        setTrackings((prev) =>
 73→          prev.map((t) => (t.id === parcel.id ? parcel : t))
 74→        );
 75→        fetchStats();
 76→      },
 77→      [fetchStats]
 78→    ),
 79→    onDelete: useCallback(
 80→      (id: string) => {
 81→        setTrackings((prev) => prev.filter((t) => t.id !== id));
 82→        fetchStats();
 83→      },
 84→      [fetchStats]
 85→    ),
 86→  });
 87→
 88→  // Refresh a single in-transit tracking
 89→  const refreshTracking = async (tracking: Parcel) => {
 90→    setRefreshingIds((prev) => new Set(prev).add(tracking.id));
 91→    try {
 92→      const res = await fetch("/api/track", {
 93→        method: "POST",
 94→        headers: { "Content-Type": "application/json" },
 95→        body: JSON.stringify({
 96→          trackingNumber: tracking.tracking_number,
 97→          courierCode: tracking.courier_code,
 98→        }),
 99→      });
100→      const json = await res.json();
101→      if (json.data) {
102→        toast.success(`Updated ${tracking.tracking_number}`);
103→        fetchStats(); // Refetch all stats
104→      } else {
105→        toast.error(json.error || "Failed to refresh");
106→      }
107→    } catch {
108→      toast.error("Failed to refresh tracking");
109→    } finally {
110→      setRefreshingIds((prev) => {
111→        const next = new Set(prev);
112→        next.delete(tracking.id);
113→        return next;
114→      });
115→    }
116→  };
117→
118→  if (!stats) {
119→    return <DashboardSkeleton />;
120→  }
121→
122→  return (
123→    <div className="space-y-6">
124→      {/* Summary cards */}
125→      <div className="grid grid-cols-2 gap-4 sm:grid-cols-4">
126→        <StatCard label="Total Trackings" value={String(stats.summary.total)} />
127→        <StatCard
128→          label="In Transit"
129→          value={String(stats.summary.inTransit)}
130→          color="amber"
131→        />
132→        <StatCard
133→          label="Delivered This Month"
134→          value={String(stats.summary.deliveredThisMonth)}
135→          color="green"
136→        />
137→        <StatCard
138→          label="Avg Delivery Time"
139→          value={
140→            stats.summary.avgDeliveryDays > 0
141→              ? `${stats.summary.avgDeliveryDays}d`
142→              : "—"
143→          }
144→          color="blue"
145→        />
146→      </div>
147→
148→      {/* Charts row */}
149→      <div className="grid gap-6 lg:grid-cols-2">
150→        <VolumeChart data={stats.dailyVolume} />
151→        <CourierChart data={stats.courierBreakdown} />
152→      </div>
153→
154→      {/* In-transit parcels */}
155→      <div>
156→        <div className="mb-3 flex items-center justify-between">
157→          <h2 className="text-lg font-semibold">In-Transit Parcels</h2>
158→          <Link
159→            href="/history"
160→            className="text-sm text-zinc-500 hover:text-zinc-700 dark:text-zinc-400 dark:hover:text-zinc-200"
161→          >
162→            View all history
163→          </Link>
164→        </div>
165→
166→        {stats.inTransitParcels.length === 0 ? (
167→          <div className="rounded-lg border border-dashed border-zinc-300 py-8 text-center dark:border-zinc-700">
168→            <p className="text-sm text-zinc-500 dark:text-zinc-400">
169→              No parcels currently in transit.
170→            </p>
171→          </div>
172→        ) : (
173→          <div className="space-y-2">
174→            {stats.inTransitParcels.map((tracking) => (
175→              <div
176→                key={tracking.id}
177→                className="flex items-center gap-4 rounded-lg border border-zinc-200 px-4 py-3 dark:border-zinc-800"
178→              >
179→                <div className="min-w-0 flex-1">
180→                  <p className="truncate font-mono text-sm font-medium">
181→                    {tracking.tracking_number}
182→                  </p>
183→                  {tracking.last_event && (
184→                    <p className="truncate text-xs text-zinc-500 dark:text-zinc-400">
185→                      {tracking.last_event}
186→                    </p>
187→                  )}
188→                </div>
189→                <span className="hidden text-xs text-zinc-500 sm:block dark:text-zinc-400">
190→                  {tracking.courier_code}
191→                </span>
192→                <StatusBadge status={tracking.status as TrackingStatus} />
193→                <button
194→                  onClick={() => refreshTracking(tracking)}
195→                  disabled={refreshingIds.has(tracking.id)}
196→                  className="rounded p-1.5 text-zinc-400 hover:bg-zinc-100 hover:text-zinc-600 disabled:opacity-50 dark:hover:bg-zinc-800 dark:hover:text-zinc-300"
197→                  title="Refresh"
198→                >
199→                  <svg
200→                    xmlns="http://www.w3.org/2000/svg"
201→                    width="14"
202→                    height="14"
203→                    viewBox="0 0 24 24"
204→                    fill="none"
205→                    stroke="currentColor"
206→                    strokeWidth="2"
207→                    strokeLinecap="round"
208→                    strokeLinejoin="round"
209→                    className={
210→                      refreshingIds.has(tracking.id) ? "animate-spin" : ""
211→                    }
212→                  >
213→                    <polyline points="23 4 23 10 17 10" />
214→                    <polyline points="1 20 1 14 7 14" />
215→                    <path d="M3.51 9a9 9 0 0 1 14.85-3.36L23 10M1 14l4.64 4.36A9 9 0 0 0 20.49 15" />
216→                  </svg>
217→                </button>
218→              </div>
219→            ))}
220→          </div>
221→        )}
222→      </div>
223→    </div>
224→  );
225→}
226→
227→function StatCard({
228→  label,
229→  value,
230→  color,
231→}: {
232→  label: string;
233→  value: string;
234→  color?: "amber" | "green" | "red" | "blue";
235→}) {
236→  const colorClasses = {
237→    amber: "text-amber-600 dark:text-amber-400",
238→    green: "text-green-600 dark:text-green-400",
239→    red: "text-red-600 dark:text-red-400",
240→    blue: "text-blue-600 dark:text-blue-400",
241→  };
242→
243→  return (
244→    <div className="rounded-lg border border-zinc-200 px-4 py-3 dark:border-zinc-800">
245→      <p className="text-xs text-zinc-500 dark:text-zinc-400">{label}</p>
246→      <p
247→        className={`mt-1 text-2xl font-bold ${color ? colorClasses[color] : ""}`}
248→      >
249→        {value}
250→      </p>
251→    </div>
252→  );
253→}
254→
255→function DashboardSkeleton() {
256→  return (
257→    <div className="space-y-6">
258→      <div className="grid grid-cols-2 gap-4 sm:grid-cols-4">
259→        {Array.from({ length: 4 }).map((_, i) => (
260→          <div
261→            key={i}
262→            className="animate-pulse rounded-lg border border-zinc-200 px-4 py-3 dark:border-zinc-800"
263→          >
264→            <div className="h-3 w-20 rounded bg-zinc-200 dark:bg-zinc-700" />
265→            <div className="mt-2 h-7 w-12 rounded bg-zinc-200 dark:bg-zinc-700" />
266→          </div>
267→        ))}
268→      </div>
269→      <div className="grid gap-6 lg:grid-cols-2">
270→        <div className="h-72 animate-pulse rounded-lg border border-zinc-200 bg-zinc-100 dark:border-zinc-800 dark:bg-zinc-900" />
271→        <div className="h-72 animate-pulse rounded-lg border border-zinc-200 bg-zinc-100 dark:border-zinc-800 dark:bg-zinc-900" />
272→      </div>
273→    </div>
274→  );
275→}
