439→function ContactsTab({ contacts }: { contacts: ContactSubmission[] }) {
440→  const [search, setSearch] = useState("");
441→  const [expanded, setExpanded] = useState<Set<string>>(new Set());
442→
443→  const filtered = useMemo(() => {
444→    if (!search) return contacts;
445→    const q = search.toLowerCase();
446→    return contacts.filter(
447→      (c) =>
448→        c.name.toLowerCase().includes(q) ||
449→        c.email.toLowerCase().includes(q) ||
450→        c.subject.toLowerCase().includes(q) ||
451→        c.message.toLowerCase().includes(q)
452→    );
453→  }, [contacts, search]);
454→
455→  const toggleExpand = (id: string) => {
456→    setExpanded((prev) => {
457→      const next = new Set(prev);
458→      if (next.has(id)) next.delete(id);
459→      else next.add(id);
460→      return next;
461→    });
462→  };
463→
464→  return (
465→    <div className="space-y-4">
466→      {/* Search */}
467→      <div className="flex items-center gap-3">
468→        <input
469→          type="text"
470→          placeholder="Search messages..."
471→          value={search}
472→          onChange={(e) => setSearch(e.target.value)}
473→          className="rounded-md border border-zinc-300 px-3 py-1.5 text-sm dark:border-zinc-700 dark:bg-zinc-900 dark:text-white"
474→        />
475→        <span className="text-xs text-zinc-500">
476→          {filtered.length} message(s)
477→        </span>
478→      </div>
479→
480→      {filtered.length === 0 ? (
481→        <p className="py-8 text-center text-sm text-zinc-500">
482→          No contact submissions found.
483→        </p>
484→      ) : (
485→        <div className="space-y-2">
486→          {filtered.map((c) => (
487→            <div
488→              key={c.id}
489→              className="rounded-lg border border-zinc-200 dark:border-zinc-800"
490→            >
491→              <button
492→                onClick={() => toggleExpand(c.id)}
493→                className="flex w-full items-center gap-4 px-4 py-3 text-left"
494→              >
495→                <div className="min-w-0 flex-1">
496→                  <p className="truncate text-sm font-medium">
497→                    {c.subject || "(No subject)"}
498→                  </p>
499→                  <p className="truncate text-xs text-zinc-500 dark:text-zinc-400">
500→                    {c.name} &lt;{c.email}&gt;
501→                  </p>
502→                </div>
503→                <span className="shrink-0 text-xs text-zinc-500 dark:text-zinc-400">
504→                  {new Date(c.created_at).toLocaleDateString("en-US", {
505→                    month: "short",
506→                    day: "numeric",
507→                    hour: "2-digit",
508→                    minute: "2-digit",
509→                  })}
510→                </span>
511→                <svg
512→                  xmlns="http://www.w3.org/2000/svg"
513→                  width="14"
514→                  height="14"
515→                  viewBox="0 0 24 24"
516→                  fill="none"
517→                  stroke="currentColor"
518→                  strokeWidth="2"
519→                  strokeLinecap="round"
520→                  strokeLinejoin="round"
521→                  className={`shrink-0 text-zinc-400 transition-transform ${
522→                    expanded.has(c.id) ? "rotate-180" : ""
523→                  }`}
524→                >
525→                  <polyline points="6 9 12 15 18 9" />
526→                </svg>
527→              </button>
528→              {expanded.has(c.id) && (
529→                <div className="border-t border-zinc-200 px-4 py-3 dark:border-zinc-800">
530→                  <div className="mb-2 flex flex-wrap gap-x-4 gap-y-1 text-xs text-zinc-500 dark:text-zinc-400">
531→                    <span>
532→                      <strong>From:</strong> {c.name} &lt;{c.email}&gt;
533→                    </span>
534→                    <span>
535→                      <strong>IP:</strong> {c.ip_address || "—"}
536→                    </span>
537→                  </div>
538→                  <p className="whitespace-pre-wrap text-sm text-zinc-700 dark:text-zinc-300">
539→                    {c.message}
540→                  </p>
541→                </div>
542→              )}
543→            </div>
544→          ))}
545→        </div>
546→      )}
547→    </div>
548→  );
