  1→"use client";
  2→
  3→import { useState, useCallback, useRef } from "react";
  4→import {
  5→  DndContext,
  6→  closestCenter,
  7→  KeyboardSensor,
  8→  PointerSensor,
  9→  useSensor,
 10→  useSensors,
 11→  type DragEndEvent,
 12→} from "@dnd-kit/core";
 13→import {
 14→  arrayMove,
 15→  SortableContext,
 16→  sortableKeyboardCoordinates,
 17→  verticalListSortingStrategy,
 18→} from "@dnd-kit/sortable";
 19→import { SortableTrackingField } from "@/components/sortable-tracking-field";
 20→import type { TrackingField, TrackingResult } from "@/types";
 21→import { useAuth } from "@/components/auth-provider";
 22→
 23→const MAX_FIELDS = 6;
 24→const GUEST_MAX_FIELDS = 1;
 25→
 26→function createEmptyField(): TrackingField {
 27→  return {
 28→    id: crypto.randomUUID(),
 29→    trackingNumber: "",
 30→    courierCode: "",
 31→    detectedCouriers: [],
 32→    isDetecting: false,
 33→  };
 34→}
 35→
 36→interface TrackingFormProps {
 37→  onResults: (results: TrackingResult[]) => void;
 38→}
 39→
 40→export function TrackingForm({ onResults }: TrackingFormProps) {
 41→  const { user } = useAuth();
 42→  const maxFields = user ? MAX_FIELDS : GUEST_MAX_FIELDS;
 43→  const [fields, setFields] = useState<TrackingField[]>([createEmptyField()]);
 44→  const [isTracking, setIsTracking] = useState(false);
 45→  const detectTimers = useRef<Map<string, NodeJS.Timeout>>(new Map());
 46→
 47→  const sensors = useSensors(
 48→    useSensor(PointerSensor),
 49→    useSensor(KeyboardSensor, {
 50→      coordinateGetter: sortableKeyboardCoordinates,
 51→    })
 52→  );
 53→
 54→  const updateField = useCallback(
 55→    (id: string, updates: Partial<TrackingField>) => {
 56→      setFields((prev) =>
 57→        prev.map((f) => (f.id === id ? { ...f, ...updates } : f))
 58→      );
 59→    },
 60→    []
 61→  );
 62→
 63→  const handleTrackingNumberChange = useCallback(
 64→    (id: string, value: string) => {
 65→      updateField(id, { trackingNumber: value, courierCode: "" });
 66→
 67→      // Debounced courier detection
 68→      const existing = detectTimers.current.get(id);
 69→      if (existing) clearTimeout(existing);
 70→
 71→      if (value.length >= 6) {
 72→        updateField(id, { isDetecting: true });
 73→        const timer = setTimeout(async () => {
 74→          try {
 75→            const res = await fetch("/api/track/detect", {
 76→              method: "POST",
 77→              headers: { "Content-Type": "application/json" },
 78→              body: JSON.stringify({ trackingNumber: value }),
 79→            });
 80→            const json = await res.json();
 81→            const couriers = json.data || [];
 82→            updateField(id, {
 83→              detectedCouriers: couriers,
 84→              courierCode: couriers[0]?.code || "",
 85→              courierName: couriers[0]?.name || "",
 86→              isDetecting: false,
 87→            });
 88→          } catch {
 89→            updateField(id, { isDetecting: false });
 90→          }
 91→        }, 500);
 92→        detectTimers.current.set(id, timer);
 93→      } else {
 94→        updateField(id, {
 95→          detectedCouriers: [],
 96→          isDetecting: false,
 97→        });
 98→      }
 99→    },
100→    [updateField]
101→  );
102→
103→  const addField = () => {
104→    if (fields.length < maxFields) {
105→      setFields((prev) => [...prev, createEmptyField()]);
106→    }
107→  };
108→
109→  const removeField = (id: string) => {
110→    if (fields.length > 1) {
111→      setFields((prev) => prev.filter((f) => f.id !== id));
112→    }
113→  };
114→
115→  const handleDragEnd = (event: DragEndEvent) => {
116→    const { active, over } = event;
117→    if (over && active.id !== over.id) {
118→      setFields((prev) => {
119→        const oldIndex = prev.findIndex((f) => f.id === active.id);
120→        const newIndex = prev.findIndex((f) => f.id === over.id);
121→        return arrayMove(prev, oldIndex, newIndex);
122→      });
123→    }
124→  };
125→
126→  const handleTrack = async () => {
127→    const validFields = fields.filter(
128→      (f) => f.trackingNumber.trim() && f.courierCode
129→    );
130→
131→    if (!validFields.length) return;
132→
133→    setIsTracking(true);
134→    const results: TrackingResult[] = [];
135→
136→    for (const field of validFields) {
137→      try {
138→        const res = await fetch("/api/track", {
139→          method: "POST",
140→          headers: { "Content-Type": "application/json" },
141→          body: JSON.stringify({
142→            trackingNumber: field.trackingNumber.trim(),
143→            courierCode: field.courierCode,
144→          }),
145→        });
146→        const json = await res.json();
147→        if (json.data) {
148→          results.push(json.data);
149→        } else if (json.error) {
150→          results.push({
151→            tracking_number: field.trackingNumber,
152→            courier_code: field.courierCode,
153→            status: "notfound",
154→            last_event: json.error,
155→            checkpoints: [],
156→          });
157→        }
158→      } catch {
159→        results.push({
160→          tracking_number: field.trackingNumber,
161→          courier_code: field.courierCode,
162→          status: "notfound",
163→          last_event: "Request failed",
164→          checkpoints: [],
165→        });
166→      }
167→    }
168→
169→    onResults(results);
170→    setIsTracking(false);
171→  };
172→
173→  const hasValidFields = fields.some(
174→    (f) => f.trackingNumber.trim() && f.courierCode
175→  );
176→
177→  return (
178→    <div className="space-y-4">
179→      <DndContext
180→        sensors={sensors}
181→        collisionDetection={closestCenter}
182→        onDragEnd={handleDragEnd}
183→      >
184→        <SortableContext
185→          items={fields.map((f) => f.id)}
186→          strategy={verticalListSortingStrategy}
187→        >
188→          <div className="space-y-3">
189→            {fields.map((field, index) => (
190→              <SortableTrackingField
191→                key={field.id}
192→                field={field}
193→                index={index}
194→                canRemove={fields.length > 1}
195→                canDrag={fields.length > 1}
196→                onTrackingNumberChange={handleTrackingNumberChange}
197→                onCourierChange={(id, code, name) =>
198→                  updateField(id, { courierCode: code, courierName: name })
199→                }
200→                onRemove={removeField}
201→              />
202→            ))}
203→          </div>
204→        </SortableContext>
205→      </DndContext>
206→
207→      <div className="flex items-center gap-3">
208→        {fields.length < maxFields && (
209→          <button
210→            type="button"
211→            onClick={addField}
212→            className="rounded-lg border border-dashed border-zinc-300 px-4 py-2 text-sm text-zinc-500 hover:border-zinc-400 hover:text-zinc-700 dark:border-zinc-700 dark:hover:border-zinc-600 dark:hover:text-zinc-300"
213→          >
214→            + Add tracking number
215→          </button>
216→        )}
217→
218→        <button
219→          type="button"
220→          onClick={handleTrack}
221→          disabled={!hasValidFields || isTracking}
222→          className="ml-auto rounded-lg bg-zinc-900 px-6 py-2.5 text-sm font-medium text-white hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
223→        >
224→          {isTracking ? "Tracking..." : "Track"}
225→        </button>
226→      </div>
227→
228→      {!user && (
229→        <p className="text-xs text-zinc-500 dark:text-zinc-400">
230→          Sign in to track up to {MAX_FIELDS} parcels and save your history.
231→        </p>
232→      )}
233→    </div>
234→  );
235→}
