        return arrayMove(prev, oldIndex, newIndex);
      });
    }
  };

  const handleTrack = async () => {
    const validFields = fields.filter(
      (f) => f.trackingNumber.trim() && f.courierCode
    );

    if (!validFields.length) return;

    setIsTracking(true);
    const results: TrackingResult[] = [];

