  2→import assert from "node:assert/strict";
  3→import { existsSync, readFileSync, readdirSync } from "node:fs";
  4→import { join } from "node:path";
  5→
  6→const PROJECT_ROOT = process.cwd();
  7→const ENTIRE_DIR = join(PROJECT_ROOT, ".entire");
  8→const LOGS_DIR = join(ENTIRE_DIR, "logs");
  9→const METADATA_DIR = join(ENTIRE_DIR, "metadata");
 10→const SETTINGS_FILE = join(ENTIRE_DIR, "settings.json");
 11→const LOG_FILE = join(LOGS_DIR, "entire.log");
 12→
 13→describe("Entire CLI - Conversation Storage", () => {
 14→  it("should have the .entire directory in the project root", () => {
 15→    assert.ok(
 16→      existsSync(ENTIRE_DIR),
 17→      ".entire/ directory does not exist — Entire CLI may not be initialized"
 18→    );
 19→  });
 20→
 21→  it("should have a valid settings.json", () => {
 22→    assert.ok(
 23→      existsSync(SETTINGS_FILE),
 24→      ".entire/settings.json is missing"
 25→    );
 26→
 27→    const settings = JSON.parse(readFileSync(SETTINGS_FILE, "utf-8"));
 28→    assert.ok(
 29→      typeof settings.enabled === "boolean",
 30→      "settings.json should have an 'enabled' field"
 31→    );
 32→    assert.ok(
 33→      typeof settings.strategy === "string",
 34→      "settings.json should have a 'strategy' field"
 35→    );
 36→  });
 37→
 38→  it("should have a logs directory", () => {
 39→    assert.ok(
 40→      existsSync(LOGS_DIR),
 41→      ".entire/logs/ directory does not exist"
 42→    );
 43→  });
 44→
 45→  it("should have a log file with session entries", () => {
 46→    assert.ok(
 47→      existsSync(LOG_FILE),
 48→      ".entire/logs/entire.log does not exist — no sessions recorded"
 49→    );
 50→
 51→    const content = readFileSync(LOG_FILE, "utf-8").trim();
 52→    assert.ok(
 53→      content.length > 0,
 54→      "Log file is empty — no conversations have been stored"
 55→    );
 56→  });
 57→
 58→  it("should contain session-start events in the log", () => {
 59→    const content = readFileSync(LOG_FILE, "utf-8");
 60→    const lines = content.trim().split("\n");
 61→
 62→    const sessionStartEvents = lines.filter((line) => {
 63→      try {
 64→        const entry = JSON.parse(line);
 65→        return entry.msg === "session-start";
 66→      } catch {
 67→        return false;
 68→      }
 69→    });
 70→
 71→    assert.ok(
 72→      sessionStartEvents.length > 0,
 73→      "No session-start events found — conversations are not being stored"
 74→    );
 75→  });
 76→
 77→  it("should have valid JSON entries in the log file", () => {
 78→    const content = readFileSync(LOG_FILE, "utf-8");
 79→    const lines = content.trim().split("\n").filter(Boolean);
 80→
 81→    for (const line of lines) {
 82→      assert.doesNotThrow(
 83→        () => JSON.parse(line),
 84→        `Invalid JSON in log entry: ${line.substring(0, 80)}...`
 85→      );
 86→    }
 87→  });
 88→
 89→  it("each session entry should have required fields", () => {
 90→    const content = readFileSync(LOG_FILE, "utf-8");
 91→    const lines = content.trim().split("\n").filter(Boolean);
 92→
 93→    for (const line of lines) {
 94→      const entry = JSON.parse(line);
 95→      assert.ok(entry.time, "Log entry missing 'time' field");
 96→      assert.ok(entry.level, "Log entry missing 'level' field");
 97→      assert.ok(entry.msg, "Log entry missing 'msg' field");
 98→      assert.ok(entry.component, "Log entry missing 'component' field");
 99→    }
100→  });
101→
102→  it("should have a metadata directory for session data", () => {
103→    assert.ok(
104→      existsSync(METADATA_DIR),
105→      ".entire/metadata/ directory does not exist — session metadata is not stored"
106→    );
107→  });
108→
109→  it("should record conversation lifecycle events (start → prompts → stop)", () => {
110→    const content = readFileSync(LOG_FILE, "utf-8");
111→    const lines = content.trim().split("\n").filter(Boolean);
112→    const messages = lines.map((line) => JSON.parse(line).msg);
113→
114→    assert.ok(
115→      messages.includes("session-start"),
116→      "Missing 'session-start' event"
117→    );
118→    assert.ok(
119→      messages.includes("user-prompt-submit"),
120→      "Missing 'user-prompt-submit' event"
121→    );
122→    assert.ok(
123→      messages.includes("stop"),
124→      "Missing 'stop' event"
125→    );
126→  });
127→
128→  it("session entries should reference a transcript path", () => {
129→    const content = readFileSync(LOG_FILE, "utf-8");
130→    const lines = content.trim().split("\n").filter(Boolean);
131→
132→    const entriesWithTranscript = lines.filter((line) => {
133→      const entry = JSON.parse(line);
134→      return typeof entry.transcript_path === "string";
135→    });
136→
137→    assert.ok(
