 4→// Handles the auth callback after email confirmation
 5→export async function GET(request: Request) {
 6→  const { searchParams, origin } = new URL(request.url);
 7→  const code = searchParams.get("code");
 8→  const next = searchParams.get("next") ?? "/dashboard";
 9→
10→  if (code) {
11→    const supabase = await createClient();
12→    const { error } = await supabase.auth.exchangeCodeForSession(code);
13→    if (!error) {
14→      return NextResponse.redirect(`${origin}${next}`);
15→    }
16→  }
17→
18→  // Return to login with error if code exchange fails
19→  return NextResponse.redirect(`${origin}/login`);
20→}
