  1→"use client";
  2→
  3→import { useState, useCallback, useRef } from "react";
  4→import Papa from "papaparse";
  5→
  6→type Step = "upload" | "review" | "processing" | "summary";
  7→
  8→interface CsvRow {
  9→  id: string;
 10→  trackingNumber: string;
 11→  courierCode: string;
 12→  label: string;
 13→  valid: boolean;
 14→  error?: string;
 15→}
 16→
 17→interface ImportResult {
 18→  trackingNumber: string;
 19→  success: boolean;
 20→  error?: string;
 21→  status?: string;
 22→}
 23→
 24→const BATCH_SIZE = 3; // Process 3 at a time to avoid rate limits
 25→
 26→export function ImportWizard() {
 27→  const [step, setStep] = useState<Step>("upload");
 28→  const [rows, setRows] = useState<CsvRow[]>([]);
 29→  const [results, setResults] = useState<ImportResult[]>([]);
 30→  const [progress, setProgress] = useState(0);
 31→  const [totalToProcess, setTotalToProcess] = useState(0);
 32→  const [isDragOver, setIsDragOver] = useState(false);
 33→  const fileInputRef = useRef<HTMLInputElement>(null);
 34→
 35→  // Parse CSV
 36→  const parseFile = useCallback((file: File) => {
 37→    Papa.parse(file, {
 38→      header: true,
 39→      skipEmptyLines: true,
 40→      transformHeader: (h: string) => h.trim().toLowerCase().replace(/\s+/g, "_"),
 41→      complete: (result) => {
 42→        const parsed: CsvRow[] = (result.data as Record<string, string>[]).map(
 43→          (row, i) => {
 44→            const trackingNumber = (
 45→              row.tracking_number ||
 46→              row.trackingnumber ||
 47→              row.tracking ||
 48→              row.number ||
 49→              ""
 50→            ).trim();
 51→            const courierCode = (
 52→              row.courier_code ||
 53→              row.couriercode ||
 54→              row.courier ||
 55→              row.carrier ||
 56→              ""
 57→            ).trim();
 58→            const label = (row.label || row.name || row.description || "").trim();
 59→
 60→            let valid = true;
 61→            let error: string | undefined;
 62→
 63→            if (!trackingNumber) {
 64→              valid = false;
 65→              error = "Missing tracking number";
 66→            } else if (!courierCode) {
 67→              valid = false;
 68→              error = "Missing courier code";
 69→            }
 70→
 71→            return {
 72→              id: `row-${i}`,
 73→              trackingNumber,
 74→              courierCode,
 75→              label,
 76→              valid,
 77→              error,
 78→            };
 79→          }
 80→        );
 81→
 82→        setRows(parsed);
 83→        setStep("review");
 84→      },
 85→      error: () => {
 86→        setRows([]);
 87→      },
 88→    });
 89→  }, []);
 90→
 91→  const handleDrop = useCallback(
 92→    (e: React.DragEvent) => {
 93→      e.preventDefault();
 94→      setIsDragOver(false);
 95→      const file = e.dataTransfer.files[0];
 96→      if (file && file.name.endsWith(".csv")) {
 97→        parseFile(file);
 98→      }
 99→    },
100→    [parseFile]
101→  );
102→
103→  const handleFileChange = useCallback(
104→    (e: React.ChangeEvent<HTMLInputElement>) => {
105→      const file = e.target.files?.[0];
106→      if (file) parseFile(file);
107→    },
108→    [parseFile]
109→  );
110→
111→  const removeRow = (id: string) => {
112→    setRows((prev) => prev.filter((r) => r.id !== id));
113→  };
114→
115→  const updateRow = (id: string, field: keyof CsvRow, value: string) => {
116→    setRows((prev) =>
117→      prev.map((r) => {
118→        if (r.id !== id) return r;
119→        const updated = { ...r, [field]: value };
120→        // Revalidate
121→        if (updated.trackingNumber && updated.courierCode) {
122→          updated.valid = true;
123→          updated.error = undefined;
124→        } else {
125→          updated.valid = false;
126→          updated.error = !updated.trackingNumber
127→            ? "Missing tracking number"
128→            : "Missing courier code";
129→        }
130→        return updated;
131→      })
132→    );
133→  };
134→
135→  // Process in batches
136→  const processImport = async () => {
137→    const validRows = rows.filter((r) => r.valid);
138→    setTotalToProcess(validRows.length);
139→    setProgress(0);
140→    setResults([]);
141→    setStep("processing");
142→
143→    const allResults: ImportResult[] = [];
144→
145→    for (let i = 0; i < validRows.length; i += BATCH_SIZE) {
146→      const batch = validRows.slice(i, i + BATCH_SIZE);
147→
148→      const batchResults = await Promise.all(
149→        batch.map(async (row) => {
150→          try {
151→            const res = await fetch("/api/import", {
152→              method: "POST",
153→              headers: { "Content-Type": "application/json" },
154→              body: JSON.stringify({
155→                trackingNumber: row.trackingNumber,
156→                courierCode: row.courierCode,
157→                label: row.label,
158→              }),
159→            });
160→            const json = await res.json();
161→            return {
162→              trackingNumber: row.trackingNumber,
163→              success: json.success ?? false,
164→              error: json.error,
165→              status: json.status,
166→            };
167→          } catch {
168→            return {
169→              trackingNumber: row.trackingNumber,
170→              success: false,
171→              error: "Request failed",
172→            };
173→          }
174→        })
175→      );
176→
177→      allResults.push(...batchResults);
178→      setResults([...allResults]);
179→      setProgress(Math.min(allResults.length, validRows.length));
180→    }
181→
182→    setStep("summary");
183→  };
184→
185→  const validCount = rows.filter((r) => r.valid).length;
186→  const invalidCount = rows.filter((r) => !r.valid).length;
187→
188→  return (
189→    <div className="space-y-6">
