199→            onDrop={handleDrop}
200→            onClick={() => fileInputRef.current?.click()}
201→            className={`flex cursor-pointer flex-col items-center justify-center rounded-lg border-2 border-dashed py-16 transition-colors ${
202→              isDragOver
203→                ? "border-zinc-500 bg-zinc-50 dark:border-zinc-400 dark:bg-zinc-900"
204→                : "border-zinc-300 hover:border-zinc-400 dark:border-zinc-700 dark:hover:border-zinc-600"
205→            }`}
206→          >
207→            <svg
208→              xmlns="http://www.w3.org/2000/svg"
209→              width="40"
210→              height="40"
211→              viewBox="0 0 24 24"
212→              fill="none"
213→              stroke="currentColor"
214→              strokeWidth="1.5"
215→              strokeLinecap="round"
216→              strokeLinejoin="round"
217→              className="text-zinc-400"
218→            >
219→              <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
220→              <polyline points="17 8 12 3 7 8" />
