 24→  last_event?: string;
 25→  user_id?: string;
 26→  created_at: string;
 27→  updated_at: string;
 28→}
 29→
 30→interface ContactSubmission {
 31→  id: string;
 32→  name: string;
 33→  email: string;
 34→  subject: string;
 35→  message: string;
 36→  ip_address: string;
 37→  created_at: string;
 38→}
 39→
 40→interface AdminStats {
 41→  totalUsers: number;
 42→  totalTrackings: number;
 43→  totalContacts: number;
 44→  mostActive: AdminUser[];
 45→  statusBreakdown: Record<string, number>;
 46→  courierBreakdown: Record<string, number>;
 47→}
 48→
 49→interface AdminData {
 50→  users: AdminUser[];
 51→  trackings: AdminTracking[];
 52→  contacts: ContactSubmission[];
 53→  stats: AdminStats;
 54→}
 55→
 56→type Tab = "overview" | "users" | "trackings" | "contacts";
 57→
 58→// ---- Component ----
 59→
 60→export function AdminClient() {
 61→  const [data, setData] = useState<AdminData | null>(null);
 62→  const [loading, setLoading] = useState(true);
 63→  const [tab, setTab] = useState<Tab>("overview");
 64→
 65→  const fetchData = useCallback(async () => {
 66→    try {
 67→      const res = await fetch("/api/admin");
 68→      if (res.ok) {
 69→        setData(await res.json());
 70→      }
 71→    } catch {
 72→      // silently fail
 73→    } finally {
 74→      setLoading(false);
 75→    }
 76→  }, []);
 77→
 78→  useEffect(() => {
 79→    fetchData();
 80→  }, [fetchData]);
 81→
 82→  if (loading) return <AdminSkeleton />;
 83→  if (!data) {
 84→    return (
 85→      <p className="py-12 text-center text-sm text-zinc-500">
 86→        Failed to load admin data.
 87→      </p>
 88→    );
 89→  }
 90→
 91→  const tabs: { key: Tab; label: string; count?: number }[] = [
 92→    { key: "overview", label: "Overview" },
 93→    { key: "users", label: "Users", count: data.stats.totalUsers },
 94→    { key: "trackings", label: "Trackings", count: data.stats.totalTrackings },
 95→    { key: "contacts", label: "Messages", count: data.stats.totalContacts },
 96→  ];
 97→
 98→  return (
 99→    <div className="space-y-6">
100→      {/* Tabs */}
101→      <div className="flex gap-1 overflow-x-auto border-b border-zinc-200 dark:border-zinc-800">
102→        {tabs.map((t) => (
103→          <button
104→            key={t.key}
105→            onClick={() => setTab(t.key)}
106→            className={`whitespace-nowrap px-4 py-2.5 text-sm font-medium transition-colors ${
107→              tab === t.key
108→                ? "border-b-2 border-zinc-900 text-zinc-900 dark:border-white dark:text-white"
109→                : "text-zinc-500 hover:text-zinc-700 dark:text-zinc-400 dark:hover:text-zinc-200"
