440→function ContactsTab({ contacts }: { contacts: ContactSubmission[] }) {
441→  const [search, setSearch] = useState("");
442→  const [expanded, setExpanded] = useState<Set<string>>(new Set());
443→
444→  const filtered = useMemo(() => {
445→    if (!search) return contacts;
446→    const q = search.toLowerCase();
