454→  }, [contacts, search]);
455→
456→  const toggleExpand = (id: string) => {
457→    setExpanded((prev) => {
458→      const next = new Set(prev);
459→      if (next.has(id)) next.delete(id);
460→      else next.add(id);
461→      return next;
462→    });
463→  };
464→
465→  return (
466→    <div className="space-y-4">
467→      {/* Search */}
468→      <div className="flex items-center gap-3">
469→        <input
470→          type="text"
471→          placeholder="Search messages..."
