484→        </p>
485→      ) : (
486→        <div className="space-y-2">
487→          {filtered.map((c) => (
488→            <div
489→              key={c.id}
490→              className="rounded-lg border border-zinc-200 dark:border-zinc-800"
491→            >
492→              <button
493→                onClick={() => toggleExpand(c.id)}
494→                className="flex w-full items-center gap-4 px-4 py-3 text-left"
495→              >
496→                <div className="min-w-0 flex-1">
497→                  <p className="truncate text-sm font-medium">
