  3→import { useSortable } from "@dnd-kit/sortable";
  4→import { CSS } from "@dnd-kit/utilities";
  5→import { GripVertical, X, Loader2 } from "lucide-react";
  6→import type { TrackingField } from "@/types";
  7→
  8→interface Props {
  9→  field: TrackingField;
 10→  index: number;
 11→  canRemove: boolean;
 12→  canDrag: boolean;
 13→  onTrackingNumberChange: (id: string, value: string) => void;
 14→  onCourierChange: (id: string, code: string, name?: string) => void;
 15→  onRemove: (id: string) => void;
 16→}
 17→
 18→export function SortableTrackingField({
 19→  field,
 20→  index,
 21→  canRemove,
 22→  canDrag,
 23→  onTrackingNumberChange,
 24→  onCourierChange,
 25→  onRemove,
 26→}: Props) {
 27→  const {
 28→    attributes,
 29→    listeners,
 30→    setNodeRef,
 31→    transform,
 32→    transition,
 33→    isDragging,
 34→  } = useSortable({ id: field.id });
 35→
 36→  const style = {
 37→    transform: CSS.Transform.toString(transform),
 38→    transition,
 39→    opacity: isDragging ? 0.5 : 1,
 40→  };
 41→
 42→  return (
 43→    <div
 44→      ref={setNodeRef}
 45→      style={style}
 46→      className="flex items-start gap-2 rounded-xl border border-zinc-200 bg-white p-3 shadow-sm dark:border-zinc-800 dark:bg-zinc-950"
 47→    >
 48→      {/* Drag handle */}
 49→      {canDrag && (
 50→        <button
 51→          {...attributes}
 52→          {...listeners}
 53→          className="mt-2 cursor-grab touch-none text-zinc-400 transition-colors hover:text-zinc-600 active:cursor-grabbing dark:hover:text-zinc-300"
 54→          aria-label="Drag to reorder"
 55→        >
 56→          <GripVertical className="h-4 w-4" />
 57→        </button>
 58→      )}
 59→
 60→      {/* Field number */}
