  1→"use client";
  2→
  3→import { useState, useCallback, useRef } from "react";
  4→import {
  5→  DndContext,
  6→  closestCenter,
  7→  KeyboardSensor,
  8→  PointerSensor,
  9→  useSensor,
 10→  useSensors,
 11→  type DragEndEvent,
 12→} from "@dnd-kit/core";
 13→import {
 14→  arrayMove,
 15→  SortableContext,
 16→  sortableKeyboardCoordinates,
 17→  verticalListSortingStrategy,
 18→} from "@dnd-kit/sortable";
 19→import { SortableTrackingField } from "@/components/sortable-tracking-field";
 20→import { Plus, Search, Loader2 } from "lucide-react";
 21→import type { TrackingField, TrackingResult } from "@/types";
 22→import { useAuth } from "@/components/auth-provider";
 23→
 24→const MAX_FIELDS = 6;
 25→const GUEST_MAX_FIELDS = 1;
 26→
 27→function createEmptyField(): TrackingField {
 28→  return {
 29→    id: crypto.randomUUID(),
 30→    trackingNumber: "",
 31→    courierCode: "",
 32→    detectedCouriers: [],
 33→    isDetecting: false,
 34→  };
 35→}
 36→
 37→interface TrackingFormProps {
 38→  onResults: (results: TrackingResult[]) => void;
 39→}
 40→
 41→export function TrackingForm({ onResults }: TrackingFormProps) {
 42→  const { user } = useAuth();
 43→  const maxFields = user ? MAX_FIELDS : GUEST_MAX_FIELDS;
 44→  const [fields, setFields] = useState<TrackingField[]>([createEmptyField()]);
 45→  const [isTracking, setIsTracking] = useState(false);
 46→  const detectTimers = useRef<Map<string, NodeJS.Timeout>>(new Map());
 47→
 48→  const sensors = useSensors(
 49→    useSensor(PointerSensor),
 50→    useSensor(KeyboardSensor, {
 51→      coordinateGetter: sortableKeyboardCoordinates,
 52→    })
 53→  );
 54→
 55→  const updateField = useCallback(
 56→    (id: string, updates: Partial<TrackingField>) => {
 57→      setFields((prev) =>
 58→        prev.map((f) => (f.id === id ? { ...f, ...updates } : f))
 59→      );
 60→    },
 61→    []
 62→  );
 63→
 64→  const handleTrackingNumberChange = useCallback(
 65→    (id: string, value: string) => {
 66→      updateField(id, { trackingNumber: value, courierCode: "" });
 67→
 68→      // Debounced courier detection
 69→      const existing = detectTimers.current.get(id);
 70→      if (existing) clearTimeout(existing);
 71→
 72→      if (value.length >= 6) {
 73→        updateField(id, { isDetecting: true });
 74→        const timer = setTimeout(async () => {
 75→          try {
 76→            const res = await fetch("/api/track/detect", {
 77→              method: "POST",
 78→              headers: { "Content-Type": "application/json" },
 79→              body: JSON.stringify({ trackingNumber: value }),
 80→            });
 81→            const json = await res.json();
 82→            const couriers = json.data || [];
 83→            updateField(id, {
 84→              detectedCouriers: couriers,
 85→              courierCode: couriers[0]?.code || "",
 86→              courierName: couriers[0]?.name || "",
 87→              isDetecting: false,
 88→            });
 89→          } catch {
 90→            updateField(id, { isDetecting: false });
 91→          }
 92→        }, 500);
 93→        detectTimers.current.set(id, timer);
 94→      } else {
 95→        updateField(id, {
 96→          detectedCouriers: [],
 97→          isDetecting: false,
 98→        });
 99→      }
100→    },
101→    [updateField]
102→  );
103→
104→  const addField = () => {
105→    if (fields.length < maxFields) {
106→      setFields((prev) => [...prev, createEmptyField()]);
107→    }
108→  };
109→
110→  const removeField = (id: string) => {
111→    if (fields.length > 1) {
112→      setFields((prev) => prev.filter((f) => f.id !== id));
113→    }
114→  };
115→
116→  const handleDragEnd = (event: DragEndEvent) => {
117→    const { active, over } = event;
118→    if (over && active.id !== over.id) {
119→      setFields((prev) => {
120→        const oldIndex = prev.findIndex((f) => f.id === active.id);
121→        const newIndex = prev.findIndex((f) => f.id === over.id);
122→        return arrayMove(prev, oldIndex, newIndex);
123→      });
124→    }
125→  };
126→
127→  const handleTrack = async () => {
128→    const validFields = fields.filter(
129→      (f) => f.trackingNumber.trim() && f.courierCode
130→    );
131→
132→    if (!validFields.length) return;
133→
134→    setIsTracking(true);
135→    const results: TrackingResult[] = [];
136→
137→    for (const field of validFields) {
138→      try {
139→        const res = await fetch("/api/track", {
140→          method: "POST",
141→          headers: { "Content-Type": "application/json" },
142→          body: JSON.stringify({
143→            trackingNumber: field.trackingNumber.trim(),
144→            courierCode: field.courierCode,
145→          }),
146→        });
147→        const json = await res.json();
148→        if (json.data) {
149→          results.push(json.data);
150→        } else if (json.error) {
151→          results.push({
152→            tracking_number: field.trackingNumber,
153→            courier_code: field.courierCode,
154→            status: "notfound",
155→            last_event: json.error,
156→            checkpoints: [],
157→          });
158→        }
159→      } catch {
160→        results.push({
161→          tracking_number: field.trackingNumber,
162→          courier_code: field.courierCode,
163→          status: "notfound",
164→          last_event: "Request failed",
165→          checkpoints: [],
166→        });
167→      }
168→    }
169→
170→    onResults(results);
171→    setIsTracking(false);
172→  };
173→
174→  const hasValidFields = fields.some(
175→    (f) => f.trackingNumber.trim() && f.courierCode
176→  );
177→
178→  return (
179→    <div className="space-y-4">
180→      <DndContext
181→        sensors={sensors}
182→        collisionDetection={closestCenter}
183→        onDragEnd={handleDragEnd}
184→      >
185→        <SortableContext
186→          items={fields.map((f) => f.id)}
187→          strategy={verticalListSortingStrategy}
188→        >
189→          <div className="space-y-3">
190→            {fields.map((field, index) => (
191→              <SortableTrackingField
192→                key={field.id}
193→                field={field}
194→                index={index}
195→                canRemove={fields.length > 1}
196→                canDrag={fields.length > 1}
197→                onTrackingNumberChange={handleTrackingNumberChange}
198→                onCourierChange={(id, code, name) =>
199→                  updateField(id, { courierCode: code, courierName: name })
200→                }
201→                onRemove={removeField}
202→              />
203→            ))}
204→          </div>
205→        </SortableContext>
206→      </DndContext>
207→
208→      <div className="flex items-center gap-3">
209→        {fields.length < maxFields && (
210→          <button
211→            type="button"
212→            onClick={addField}
213→            className="inline-flex items-center gap-1.5 rounded-lg border border-dashed border-zinc-300 px-4 py-2 text-sm text-zinc-500 transition-colors hover:border-zinc-400 hover:text-zinc-700 dark:border-zinc-700 dark:hover:border-zinc-600 dark:hover:text-zinc-300"
214→          >
215→            <Plus className="h-4 w-4" />
216→            Add tracking number
217→          </button>
218→        )}
219→
220→        <button
221→          type="button"
222→          onClick={handleTrack}
223→          disabled={!hasValidFields || isTracking}
224→          className="ml-auto inline-flex items-center gap-2 rounded-full bg-zinc-900 px-6 py-2.5 text-sm font-medium text-white shadow-sm transition-all hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
225→        >
226→          {isTracking ? (
227→            <Loader2 className="h-4 w-4 animate-spin" />
228→          ) : (
229→            <Search className="h-4 w-4" />
230→          )}
231→          {isTracking ? "Tracking..." : "Track"}
232→        </button>
233→      </div>
234→
235→      {!user && (
236→        <p className="text-xs text-zinc-500 dark:text-zinc-400">
237→          Sign in to track up to {MAX_FIELDS} parcels and save your history.
238→        </p>
239→      )}
240→    </div>
241→  );
242→}
243→

 1→import { NextRequest, NextResponse } from "next/server";
 2→import { nanoid } from "nanoid";
 3→import { trackParcel } from "@/lib/trackingmore";
 4→import { createClient } from "@/lib/supabase/server";
 5→
 6→export async function POST(request: NextRequest) {
 7→  try {
 8→    const body = await request.json();
 9→    const { trackingNumber, courierCode } = body;
10→
11→    if (!trackingNumber || !courierCode) {
12→      return NextResponse.json(
13→        { error: "Tracking number and courier code are required" },
14→        { status: 400 }
15→      );
16→    }
17→
18→    // Call TrackingMore API (secrets stay server-side)
19→    const result = await trackParcel(trackingNumber, courierCode);
20→
21→    // If user is logged in, save to database
22→    const supabase = await createClient();
23→    const {
24→      data: { user },
25→    } = await supabase.auth.getUser();
26→
27→    if (user) {
28→      // Check if tracking already exists for this user
29→      const { data: existing, error: lookupError } = await supabase
30→        .from("trackings")
31→        .select("id")
32→        .eq("user_id", user.id)
33→        .eq("tracking_number", trackingNumber)
34→        .eq("courier_code", courierCode)
35→        .single();
36→
37→      if (existing && !lookupError) {
38→        // Update existing tracking
39→        const { error: updateError } = await supabase
40→          .from("trackings")
41→          .update({
42→            status: result.status,
43→            last_event: result.last_event || null,
44→            origin: result.origin || null,
45→            destination: result.destination || null,
46→            checkpoints: result.checkpoints,
47→          })
48→          .eq("id", existing.id);
49→        if (updateError) {
50→          console.error("Failed to update tracking:", updateError.message);
51→        }
52→      } else {
53→        // Insert new tracking with unique public slug
54→        const { error: insertError } = await supabase.from("trackings").insert({
55→          user_id: user.id,
56→          tracking_number: result.tracking_number,
57→          courier_code: result.courier_code,
58→          status: result.status,
59→          last_event: result.last_event || null,
60→          origin: result.origin || null,
61→          destination: result.destination || null,
62→          checkpoints: result.checkpoints,
63→          public_slug: nanoid(10),
64→        });
65→        if (insertError) {
66→          console.error("Failed to insert tracking:", insertError.message);
67→        }
68→      }
69→    }
70→
71→    return NextResponse.json({ data: result });
72→  } catch (error) {
73→    const message =
74→      error instanceof Error ? error.message : "Failed to track parcel";
75→    return NextResponse.json({ error: message }, { status: 500 });
76→  }
77→}
