 1→import { createClient } from "@/lib/supabase/server";
 2→import { HistoryList } from "@/components/history-list";
 3→import { HistorySkeleton } from "@/components/history-skeleton";
 4→import { Suspense } from "react";
 5→import type { Parcel } from "@/types";
 6→
 7→export default function HistoryPage() {
 8→  return (
 9→    <div className="mx-auto max-w-5xl px-4 py-8">
10→      <div className="mb-6">
11→        <h1 className="text-2xl font-bold tracking-tight">Tracking History</h1>
12→        <p className="mt-1 text-sm text-zinc-500 dark:text-zinc-400">
13→          View and manage all your tracked parcels.
14→        </p>
15→      </div>
16→
17→      <Suspense fallback={<HistorySkeleton />}>
18→        <HistoryData />
19→      </Suspense>
20→    </div>
21→  );
22→}
23→
24→async function HistoryData() {
25→  const supabase = await createClient();
26→  const {
27→    data: { user },
28→  } = await supabase.auth.getUser();
29→
30→  if (!user) return null;
31→
32→  const { data: trackings } = await supabase
33→    .from("trackings")
34→    .select("*")
35→    .eq("user_id", user.id)
36→    .order("updated_at", { ascending: false });
37→
38→  return <HistoryList initialTrackings={(trackings as Parcel[]) || []} />;
39→}
