  1→"use client";
  2→
  3→import { useState, useCallback } from "react";
  4→import { createClient } from "@/lib/supabase/client";
  5→import { StatusBadge } from "@/components/status-badge";
  6→import { HistoryEmpty } from "@/components/history-empty";
  7→import { useRealtimeTrackings } from "@/lib/use-realtime-trackings";
  8→import { useAuth } from "@/components/auth-provider";
  9→import type { Parcel, TrackingStatus } from "@/types";
 10→import { toast } from "sonner";
 11→import { Share2, Lock, RefreshCw, Trash2 } from "lucide-react";
 12→
 13→const STATUS_OPTIONS: { value: string; label: string }[] = [
 14→  { value: "all", label: "All" },
 15→  { value: "transit", label: "In Transit" },
 16→  { value: "delivered", label: "Delivered" },
 17→  { value: "pending", label: "Pending" },
 18→  { value: "pickup", label: "Picked Up" },
 19→  { value: "exception", label: "Exception" },
 20→  { value: "expired", label: "Expired" },
 21→  { value: "notfound", label: "Not Found" },
 22→];
 23→
 24→type SortOrder = "newest" | "oldest";
 25→
 26→interface HistoryListProps {
 27→  initialTrackings: Parcel[];
 28→}
 29→
 30→export function HistoryList({ initialTrackings }: HistoryListProps) {
 31→  const [trackings, setTrackings] = useState<Parcel[]>(initialTrackings);
 32→  const [statusFilter, setStatusFilter] = useState("all");
 33→  const [sortOrder, setSortOrder] = useState<SortOrder>("newest");
 34→  const [selected, setSelected] = useState<Set<string>>(new Set());
 35→  const [refreshingIds, setRefreshingIds] = useState<Set<string>>(new Set());
 36→  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
 37→  const supabase = createClient();
 38→  const { user } = useAuth();
 39→
 40→  // Realtime subscriptions — live updates across tabs/devices
 41→  useRealtimeTrackings({
 42→    userId: user?.id,
 43→    onInsert: useCallback((parcel: Parcel) => {
 44→      setTrackings((prev) => {
 45→        if (prev.some((t) => t.id === parcel.id)) return prev;
 46→        return [parcel, ...prev];
 47→      });
 48→      toast.info(`New tracking added: ${parcel.tracking_number}`);
 49→    }, []),
 50→    onUpdate: useCallback((parcel: Parcel) => {
 51→      setTrackings((prev) =>
 52→        prev.map((t) => (t.id === parcel.id ? parcel : t))
 53→      );
 54→    }, []),
 55→    onDelete: useCallback((id: string) => {
 56→      setTrackings((prev) => prev.filter((t) => t.id !== id));
 57→      setSelected((prev) => {
 58→        const next = new Set(prev);
 59→        next.delete(id);
 60→        return next;
 61→      });
 62→    }, []),
 63→  });
 64→
 65→  // Filter
 66→  const filtered = trackings.filter(
 67→    (t) => statusFilter === "all" || t.status === statusFilter
 68→  );
 69→
 70→  // Sort
 71→  const sorted = [...filtered].sort((a, b) => {
 72→    const dateA = new Date(a.updated_at).getTime();
 73→    const dateB = new Date(b.updated_at).getTime();
 74→    return sortOrder === "newest" ? dateB - dateA : dateA - dateB;
 75→  });
 76→
 77→  // Selection
 78→  const toggleSelect = (id: string) => {
 79→    setSelected((prev) => {
 80→      const next = new Set(prev);
 81→      if (next.has(id)) next.delete(id);
 82→      else next.add(id);
 83→      return next;
 84→    });
 85→  };
 86→
 87→  const toggleSelectAll = () => {
 88→    if (selected.size === sorted.length) {
 89→      setSelected(new Set());
 90→    } else {
 91→      setSelected(new Set(sorted.map((t) => t.id)));
 92→    }
 93→  };
 94→
 95→  // Refresh a single tracking
 96→  const refreshTracking = useCallback(
 97→    async (tracking: Parcel) => {
 98→      setRefreshingIds((prev) => new Set(prev).add(tracking.id));
 99→      try {
100→        const res = await fetch("/api/track", {
101→          method: "POST",
102→          headers: { "Content-Type": "application/json" },
103→          body: JSON.stringify({
104→            trackingNumber: tracking.tracking_number,
105→            courierCode: tracking.courier_code,
106→          }),
107→        });
108→        const json = await res.json();
109→        if (json.data) {
110→          // Update local state
111→          setTrackings((prev) =>
112→            prev.map((t) =>
113→              t.id === tracking.id
114→                ? {
115→                    ...t,
116→                    status: json.data.status,
117→                    last_event: json.data.last_event || t.last_event,
118→                    checkpoints: json.data.checkpoints || t.checkpoints,
119→                    updated_at: new Date().toISOString(),
120→                  }
121→                : t
122→            )
123→          );
124→          toast.success(`Updated ${tracking.tracking_number}`);
125→        } else {
126→          toast.error(json.error || "Failed to refresh");
127→        }
128→      } catch {
129→        toast.error("Failed to refresh tracking");
130→      } finally {
131→        setRefreshingIds((prev) => {
132→          const next = new Set(prev);
133→          next.delete(tracking.id);
134→          return next;
135→        });
136→      }
137→    },
138→    []
139→  );
140→
141→  // Toggle public/private
142→  const togglePublic = async (tracking: Parcel) => {
143→    const newValue = !tracking.is_public;
144→    const { error } = await supabase
145→      .from("trackings")
146→      .update({ is_public: newValue })
147→      .eq("id", tracking.id);
148→    if (error) {
149→      toast.error("Failed to update visibility");
150→      return;
151→    }
152→    setTrackings((prev) =>
153→      prev.map((t) =>
154→        t.id === tracking.id ? { ...t, is_public: newValue } : t
155→      )
156→    );
157→    if (newValue && tracking.public_slug) {
158→      const url = `${window.location.origin}/track/${tracking.public_slug}`;
159→      await navigator.clipboard.writeText(url);
160→      toast.success("Link copied! Tracking is now public.");
161→    } else {
162→      toast.success("Tracking is now private.");
163→    }
164→  };
165→
166→  // Delete single
167→  const deleteTracking = async (id: string) => {
168→    const { error } = await supabase.from("trackings").delete().eq("id", id);
169→    if (error) {
170→      toast.error("Failed to delete");
171→      return;
172→    }
173→    setTrackings((prev) => prev.filter((t) => t.id !== id));
174→    setSelected((prev) => {
175→      const next = new Set(prev);
176→      next.delete(id);
177→      return next;
178→    });
179→    toast.success("Tracking deleted");
180→  };
181→
182→  // Bulk delete
183→  const bulkDelete = async () => {
184→    const ids = Array.from(selected);
185→    const { error } = await supabase
186→      .from("trackings")
187→      .delete()
188→      .in("id", ids);
189→    if (error) {
190→      toast.error("Failed to delete trackings");
191→      return;
192→    }
193→    setTrackings((prev) => prev.filter((t) => !selected.has(t.id)));
194→    setSelected(new Set());
195→    setShowDeleteConfirm(false);
196→    toast.success(`${ids.length} tracking(s) deleted`);
197→  };
198→
199→  if (!trackings.length) {
200→    return <HistoryEmpty />;
201→  }
202→
203→  return (
204→    <div className="space-y-4">
205→      {/* Toolbar */}
206→      <div className="flex flex-wrap items-center gap-3">
207→        {/* Status filter */}
208→        <select
209→          value={statusFilter}
210→          onChange={(e) => setStatusFilter(e.target.value)}
211→          className="rounded-md border border-zinc-300 px-3 py-1.5 text-sm dark:border-zinc-700 dark:bg-zinc-900 dark:text-white"
212→        >
213→          {STATUS_OPTIONS.map((opt) => (
214→            <option key={opt.value} value={opt.value}>
215→              {opt.label}
216→            </option>
217→          ))}
218→        </select>
219→
220→        {/* Sort */}
221→        <select
222→          value={sortOrder}
223→          onChange={(e) => setSortOrder(e.target.value as SortOrder)}
224→          className="rounded-md border border-zinc-300 px-3 py-1.5 text-sm dark:border-zinc-700 dark:bg-zinc-900 dark:text-white"
225→        >
226→          <option value="newest">Newest first</option>
227→          <option value="oldest">Oldest first</option>
228→        </select>
229→
230→        {/* Bulk actions */}
231→        {selected.size > 0 && (
232→          <div className="ml-auto flex items-center gap-2">
233→            <span className="text-sm text-zinc-500">
234→              {selected.size} selected
235→            </span>
236→            <button
237→              onClick={() => setShowDeleteConfirm(true)}
238→              className="rounded-md bg-red-600 px-3 py-1.5 text-sm font-medium text-white hover:bg-red-700"
239→            >
240→              Delete selected
241→            </button>
242→          </div>
243→        )}
244→      </div>
245→
246→      {/* Delete confirmation modal */}
247→      {showDeleteConfirm && (
248→        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50">
249→          <div className="mx-4 w-full max-w-sm rounded-lg bg-white p-6 shadow-xl dark:bg-zinc-900">
250→            <h3 className="text-lg font-semibold">Confirm Delete</h3>
251→            <p className="mt-2 text-sm text-zinc-500 dark:text-zinc-400">
252→              Are you sure you want to delete {selected.size} tracking(s)? This
253→              action cannot be undone.
254→            </p>
255→            <div className="mt-4 flex justify-end gap-2">
256→              <button
257→                onClick={() => setShowDeleteConfirm(false)}
258→                className="rounded-md px-4 py-2 text-sm font-medium text-zinc-600 hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-800"
259→              >
260→                Cancel
261→              </button>
262→              <button
263→                onClick={bulkDelete}
264→                className="rounded-md bg-red-600 px-4 py-2 text-sm font-medium text-white hover:bg-red-700"
265→              >
266→                Delete
267→              </button>
268→            </div>
269→          </div>
270→        </div>
271→      )}
272→
273→      {/* Table header */}
274→      <div className="hidden items-center gap-4 border-b border-zinc-200 px-4 py-2 text-xs font-medium uppercase text-zinc-500 sm:flex dark:border-zinc-800">
275→        <input
276→          type="checkbox"
277→          checked={selected.size === sorted.length && sorted.length > 0}
278→          onChange={toggleSelectAll}
279→          className="h-4 w-4 rounded border-zinc-300"
280→        />
281→        <span className="flex-1">Tracking Number</span>
282→        <span className="w-28">Courier</span>
283→        <span className="w-24">Status</span>
284→        <span className="w-32">Last Updated</span>
285→        <span className="w-24">Actions</span>
286→      </div>
287→
288→      {/* Tracking rows */}
289→      <div className="space-y-2">
290→        {sorted.map((tracking) => (
291→          <div
292→            key={tracking.id}
293→            className="flex flex-col gap-3 rounded-lg border border-zinc-200 px-4 py-3 sm:flex-row sm:items-center sm:gap-4 dark:border-zinc-800"
294→          >
295→            {/* Checkbox */}
296→            <input
297→              type="checkbox"
298→              checked={selected.has(tracking.id)}
299→              onChange={() => toggleSelect(tracking.id)}
300→              className="h-4 w-4 shrink-0 rounded border-zinc-300"
301→            />
302→
303→            {/* Tracking number + last event */}
304→            <div className="min-w-0 flex-1">
305→              <p className="truncate font-mono text-sm font-medium">
306→                {tracking.tracking_number}
307→              </p>
308→              {tracking.last_event && (
309→                <p className="truncate text-xs text-zinc-500 dark:text-zinc-400">
310→                  {tracking.last_event}
311→                </p>
312→              )}
313→            </div>
314→
315→            {/* Courier */}
316→            <span className="w-28 text-sm text-zinc-600 dark:text-zinc-400">
317→              {tracking.courier_code}
318→            </span>
319→
320→            {/* Status */}
321→            <div className="w-24">
322→              <StatusBadge status={tracking.status as TrackingStatus} />
323→            </div>
324→
325→            {/* Date */}
326→            <span className="w-32 text-xs text-zinc-500 dark:text-zinc-400">
327→              {new Date(tracking.updated_at).toLocaleDateString("en-US", {
328→                month: "short",
329→                day: "numeric",
330→                hour: "2-digit",
331→                minute: "2-digit",
332→              })}
333→            </span>
334→
335→            {/* Actions */}
336→            <div className="flex w-24 items-center gap-1">
337→              <button
338→                onClick={() => togglePublic(tracking)}
339→                className={`rounded p-1.5 transition-colors ${
340→                  tracking.is_public
341→                    ? "text-blue-500 hover:bg-blue-50 dark:hover:bg-blue-950"
342→                    : "text-zinc-400 hover:bg-zinc-100 hover:text-zinc-600 dark:hover:bg-zinc-800 dark:hover:text-zinc-300"
343→                }`}
344→                title={tracking.is_public ? "Make private" : "Share publicly"}
345→              >
346→                {tracking.is_public ? (
347→                  <Share2 className="h-3.5 w-3.5" />
348→                ) : (
349→                  <Lock className="h-3.5 w-3.5" />
350→                )}
351→              </button>
352→              <button
353→                onClick={() => refreshTracking(tracking)}
354→                disabled={refreshingIds.has(tracking.id)}
355→                className="rounded p-1.5 text-zinc-400 transition-colors hover:bg-zinc-100 hover:text-zinc-600 disabled:opacity-50 dark:hover:bg-zinc-800 dark:hover:text-zinc-300"
356→                title="Refresh"
357→              >
358→                <RefreshCw
359→                  className={`h-3.5 w-3.5 ${
360→                    refreshingIds.has(tracking.id) ? "animate-spin" : ""
361→                  }`}
362→                />
363→              </button>
364→              <button
365→                onClick={() => deleteTracking(tracking.id)}
366→                className="rounded p-1.5 text-zinc-400 transition-colors hover:bg-red-50 hover:text-red-600 dark:hover:bg-red-950 dark:hover:text-red-400"
367→                title="Delete"
368→              >
369→                <Trash2 className="h-3.5 w-3.5" />
370→              </button>
371→            </div>
372→          </div>
373→        ))}
374→      </div>
375→
376→      {sorted.length === 0 && trackings.length > 0 && (
377→        <p className="py-8 text-center text-sm text-zinc-500 dark:text-zinc-400">
378→          No trackings match the selected filter.
379→        </p>
380→      )}
381→    </div>
382→  );
383→}
384→
