 1→import { NextRequest, NextResponse } from "next/server";
 2→import { nanoid } from "nanoid";
 3→import { trackParcel } from "@/lib/trackingmore";
 4→import { createClient } from "@/lib/supabase/server";
 5→
 6→export async function POST(request: NextRequest) {
 7→  try {
 8→    const body = await request.json();
 9→    const { trackingNumber, courierCode } = body;
10→
11→    if (!trackingNumber || !courierCode) {
12→      return NextResponse.json(
13→        { error: "Tracking number and courier code are required" },
14→        { status: 400 }
15→      );
16→    }
17→
18→    // Call TrackingMore API (secrets stay server-side)
19→    const result = await trackParcel(trackingNumber, courierCode);
20→
21→    // If user is logged in, save to database
22→    const supabase = await createClient();
23→    const {
24→      data: { user },
25→    } = await supabase.auth.getUser();
26→
27→    if (user) {
28→      // Check if tracking already exists for this user
29→      const { data: existing, error: lookupError } = await supabase
30→        .from("trackings")
31→        .select("id")
32→        .eq("user_id", user.id)
33→        .eq("tracking_number", trackingNumber)
34→        .eq("courier_code", courierCode)
35→        .single();
36→
37→      if (existing && !lookupError) {
38→        // Update existing tracking
39→        const { error: updateError } = await supabase
40→          .from("trackings")
41→          .update({
42→            status: result.status,
43→            last_event: result.last_event || null,
44→            origin: result.origin || null,
45→            destination: result.destination || null,
46→            checkpoints: result.checkpoints,
47→          })
48→          .eq("id", existing.id);
49→        if (updateError) {
50→          console.error("Failed to update tracking:", updateError.message);
51→        }
52→      } else {
53→        // Insert new tracking with unique public slug
54→        const { error: insertError } = await supabase.from("trackings").insert({
55→          user_id: user.id,
56→          tracking_number: result.tracking_number,
57→          courier_code: result.courier_code,
58→          status: result.status,
59→          last_event: result.last_event || null,
60→          origin: result.origin || null,
61→          destination: result.destination || null,
62→          checkpoints: result.checkpoints,
63→          public_slug: nanoid(10),
64→        });
65→        if (insertError) {
66→          console.error("Failed to insert tracking:", insertError.message);
67→        }
68→      }
69→    }
70→
71→    return NextResponse.json({ data: result });
72→  } catch (error) {
73→    const message =
74→      error instanceof Error ? error.message : "Failed to track parcel";
75→    return NextResponse.json({ error: message }, { status: 500 });
76→  }
77→}
