 1→"use client";
 2→
 3→import { useTheme } from "next-themes";
 4→import { useEffect, useState } from "react";
 5→
 6→export function ThemeToggle() {
 7→  const { theme, setTheme } = useTheme();
 8→  const [mounted, setMounted] = useState(false);
 9→
10→  useEffect(() => setMounted(true), []);
11→
12→  if (!mounted) return null;
13→
14→  return (
15→    <button
16→      onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
17→      className="rounded-md p-2 hover:bg-zinc-100 dark:hover:bg-zinc-800"
18→      aria-label="Toggle theme"
19→    >
20→      {theme === "dark" ? (
21→        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
22→          <circle cx="12" cy="12" r="5" />
23→          <line x1="12" y1="1" x2="12" y2="3" />
24→          <line x1="12" y1="21" x2="12" y2="23" />
25→          <line x1="4.22" y1="4.22" x2="5.64" y2="5.64" />
26→          <line x1="18.36" y1="18.36" x2="19.78" y2="19.78" />
