  1→"use client";
  2→
  3→import { createContext, useContext, useEffect, useState } from "react";
  4→import { createClient } from "@/lib/supabase/client";
  5→import type { User } from "@supabase/supabase-js";
  6→
  7→interface Profile {
  8→  id: string;
  9→  email: string;
 10→  role: "guest" | "user" | "admin";
 11→  created_at: string;
 12→}
 13→
 14→interface AuthContextType {
 15→  user: User | null;
 16→  profile: Profile | null;
 17→  isAdmin: boolean;
 18→  isLoading: boolean;
 19→  signOut: () => Promise<void>;
 20→}
 21→
 22→const AuthContext = createContext<AuthContextType>({
 23→  user: null,
 24→  profile: null,
 25→  isAdmin: false,
 26→  isLoading: true,
 27→  signOut: async () => {},
 28→});
 29→
 30→export function AuthProvider({ children }: { children: React.ReactNode }) {
 31→  const [user, setUser] = useState<User | null>(null);
 32→  const [profile, setProfile] = useState<Profile | null>(null);
 33→  const [isLoading, setIsLoading] = useState(true);
 34→  const supabase = createClient();
 35→
 36→  useEffect(() => {
 37→    // Get initial session
 38→    const getSession = async () => {
 39→      const {
 40→        data: { user },
 41→      } = await supabase.auth.getUser();
 42→      setUser(user);
 43→      if (user) {
 44→        await fetchProfile(user.id);
 45→      }
 46→      setIsLoading(false);
 47→    };
 48→
 49→    getSession();
 50→
 51→    // Listen for auth changes
 52→    const {
 53→      data: { subscription },
 54→    } = supabase.auth.onAuthStateChange(async (_event, session) => {
 55→      const currentUser = session?.user ?? null;
 56→      setUser(currentUser);
 57→      if (currentUser) {
 58→        await fetchProfile(currentUser.id);
 59→      } else {
 60→        setProfile(null);
 61→      }
 62→      setIsLoading(false);
 63→    });
 64→
 65→    return () => subscription.unsubscribe();
 66→  }, []);
 67→
 68→  const fetchProfile = async (userId: string) => {
 69→    const { data, error } = await supabase
 70→      .from("profiles")
 71→      .select("*")
 72→      .eq("id", userId)
 73→      .single();
 74→    if (error || !data) {
 75→      console.error("Failed to fetch profile:", error?.message);
 76→      setProfile(null);
 77→      return;
 78→    }
 79→    setProfile(data);
 80→  };
 81→
 82→  const signOut = async () => {
 83→    await supabase.auth.signOut();
 84→    setUser(null);
 85→    setProfile(null);
 86→  };
 87→
 88→  return (
 89→    <AuthContext.Provider
 90→      value={{
 91→        user,
 92→        profile,
 93→        isAdmin: profile?.role === "admin",
 94→        isLoading,
 95→        signOut,
 96→      }}
 97→    >
 98→      {children}
 99→    </AuthContext.Provider>
100→  );
101→}
102→
103→export const useAuth = () => useContext(AuthContext);
104→

 1→import { NextRequest, NextResponse } from "next/server";
 2→import { nanoid } from "nanoid";
 3→import { trackParcel } from "@/lib/trackingmore";
 4→import { createClient } from "@/lib/supabase/server";
 5→
 6→export async function POST(request: NextRequest) {
 7→  try {
 8→    const body = await request.json();
 9→    const { trackingNumber, courierCode } = body;
10→
11→    if (!trackingNumber || !courierCode) {
12→      return NextResponse.json(
13→        { error: "Tracking number and courier code are required" },
14→        { status: 400 }
15→      );
16→    }
17→
18→    // Call TrackingMore API (secrets stay server-side)
19→    const result = await trackParcel(trackingNumber, courierCode);
20→
21→    // If user is logged in, save to database
22→    const supabase = await createClient();
23→    const {
24→      data: { user },
25→    } = await supabase.auth.getUser();
26→
27→    if (user) {
28→      // Check if tracking already exists for this user
29→      const { data: existing, error: lookupError } = await supabase
30→        .from("trackings")
31→        .select("id")
32→        .eq("user_id", user.id)
33→        .eq("tracking_number", trackingNumber)
34→        .eq("courier_code", courierCode)
35→        .single();
36→
37→      if (existing && !lookupError) {
38→        // Update existing tracking
39→        const { error: updateError } = await supabase
40→          .from("trackings")
41→          .update({
42→            status: result.status,
43→            last_event: result.last_event || null,
44→            origin: result.origin || null,
45→            destination: result.destination || null,
46→            checkpoints: result.checkpoints,
47→          })
48→          .eq("id", existing.id);
49→        if (updateError) {
50→          console.error("Failed to update tracking:", updateError.message);
51→        }
52→      } else {
53→        // Insert new tracking with unique public slug
54→        const { error: insertError } = await supabase.from("trackings").insert({
55→          user_id: user.id,
56→          tracking_number: result.tracking_number,
57→          courier_code: result.courier_code,
58→          status: result.status,
59→          last_event: result.last_event || null,
60→          origin: result.origin || null,
61→          destination: result.destination || null,
62→          checkpoints: result.checkpoints,
63→          public_slug: nanoid(10),
64→        });
65→        if (insertError) {
66→          console.error("Failed to insert tracking:", insertError.message);
67→        }
68→      }
69→    }
70→
71→    return NextResponse.json({ data: result });
72→  } catch (error) {
73→    const message =
74→      error instanceof Error ? error.message : "Failed to track parcel";
75→    return NextResponse.json({ error: message }, { status: 500 });
76→  }
77→}
78→

 1→import { NextRequest, NextResponse } from "next/server";
 2→import { detectCourier } from "@/lib/trackingmore";
 3→
 4→export async function POST(request: NextRequest) {
 5→  try {
 6→    const body = await request.json();
 7→    const { trackingNumber } = body;
 8→
 9→    if (!trackingNumber) {
10→      return NextResponse.json(
11→        { error: "Tracking number is required" },
12→        { status: 400 }
13→      );
14→    }
15→
16→    const couriers = await detectCourier(trackingNumber);
17→    return NextResponse.json({ data: couriers });
18→  } catch (error) {
19→    const message =
20→      error instanceof Error ? error.message : "Failed to detect courier";
21→    return NextResponse.json({ error: message }, { status: 500 });
22→  }
23→}
