  1→import type {
  2→  TrackingResult,
  3→  TrackingCheckpoint,
  4→  DetectedCourier,
  5→} from "@/types";
  6→
  7→const API_KEY = process.env.TRACKINGMORE_API_KEY!;
  8→const BASE_URL = "https://api.trackingmore.com/v4";
  9→
 10→const headers = {
 11→  "Content-Type": "application/json",
 12→  "Tracking-Api-Key": API_KEY,
 13→};
 14→
 15→interface TrackingMoreResponse {
 16→  meta: { code: number; type: string; message: string };
 17→  data: Record<string, unknown>;
 18→}
 19→
 20→interface DetectResponse {
 21→  meta: { code: number; type: string; message: string };
 22→  data: Array<{ courier_name: string; courier_code: string }>;
 23→}
 24→
 25→/**
 26→ * Create a tracking and get real-time results (V4 "create & get").
 27→ */
 28→export async function trackParcel(
 29→  trackingNumber: string,
 30→  courierCode: string,
 31→): Promise<TrackingResult> {
 32→  const res = await fetch(`${BASE_URL}/trackings/create`, {
 33→    method: "POST",
 34→    headers,
 35→    body: JSON.stringify({
 36→      tracking_number: trackingNumber,
 37→      courier_code: courierCode,
 38→    }),
 39→  });
 40→
 41→  const json: TrackingMoreResponse = await res.json();
 42→
 43→  // If tracking already exists, try to get it instead
 44→  if (json.meta.code === 4016) {
 45→    return getTracking(trackingNumber, courierCode);
 46→  }
 47→
 48→  if (json.meta.code !== 200) {
 49→    throw new Error(json.meta.message || "Failed to track parcel");
 50→  }
 51→
 52→  return normalizeTrackingData(json.data);
 53→}
 54→
 55→/**
 56→ * Get existing tracking results.
 57→ */
 58→export async function getTracking(
 59→  trackingNumber: string,
 60→  courierCode: string,
 61→): Promise<TrackingResult> {
 62→  const res = await fetch(
 63→    `${BASE_URL}/trackings/${courierCode}/${trackingNumber}`,
 64→    { method: "GET", headers },
 65→  );
 66→
 67→  const json: TrackingMoreResponse = await res.json();
 68→
 69→  if (json.meta.code !== 200) {
 70→    throw new Error(json.meta.message || "Failed to get tracking");
 71→  }
 72→
 73→  return normalizeTrackingData(json.data);
 74→}
 75→
 76→/**
 77→ * Detect possible couriers for a tracking number.
 78→ */
 79→export async function detectCourier(
 80→  trackingNumber: string,
 81→): Promise<DetectedCourier[]> {
 82→  const res = await fetch(`${BASE_URL}/couriers/detect`, {
 83→    method: "POST",
 84→    headers,
 85→    body: JSON.stringify({ tracking_number: trackingNumber }),
 86→  });
 87→
 88→  const json: DetectResponse = await res.json();
 89→
 90→  if (json.meta.code !== 200 || !json.data) {
 91→    return [];
 92→  }
 93→
 94→  return json.data.map((c) => ({ name: c.courier_name, code: c.courier_code }));
 95→}
 96→
 97→/**
 98→ * Normalize the TrackingMore API response into our internal format.
 99→ */
100→function normalizeTrackingData(data: Record<string, unknown>): TrackingResult {
101→  const checkpointsRaw = (data.origin_info as Record<string, unknown>)
102→    ?.trackinfo as Array<Record<string, unknown>> | undefined;
103→
104→  const destinationRaw = (data.destination_info as Record<string, unknown>)
105→    ?.trackinfo as Array<Record<string, unknown>> | undefined;
106→
107→  const allCheckpoints: TrackingCheckpoint[] = [];
108→
109→  // Combine origin and destination checkpoints
110→  if (destinationRaw?.length) {
111→    for (const cp of destinationRaw) {
112→      allCheckpoints.push({
113→        date: (cp.checkpoint_date as string) || (cp.Date as string) || "",
114→        description:
115→          (cp.tracking_detail as string) ||
116→          (cp.StatusDescription as string) ||
117→          "",
118→        location: (cp.location as string) || "",
119→        status: (cp.checkpoint_delivery_status as string) || "",
120→      });
121→    }
122→  }
123→
124→  if (checkpointsRaw?.length) {
125→    for (const cp of checkpointsRaw) {
126→      allCheckpoints.push({
127→        date: (cp.checkpoint_date as string) || (cp.Date as string) || "",
128→        description:
129→          (cp.tracking_detail as string) ||
130→          (cp.StatusDescription as string) ||
131→          "",
132→        location: (cp.location as string) || "",
133→        status: (cp.checkpoint_delivery_status as string) || "",
134→      });
135→    }
136→  }
137→
138→  // Sort checkpoints by date (newest first)
139→  allCheckpoints.sort(
140→    (a, b) => new Date(b.date).getTime() - new Date(a.date).getTime(),
141→  );
142→
143→  return {
144→    tracking_number: data.tracking_number as string,
145→    courier_code: data.courier_code as string,
146→    courier_name: (data.courier_name as string) || undefined,
147→    status: (data.delivery_status as TrackingResult["status"]) || "pending",
148→    last_event: allCheckpoints[0]?.description || undefined,
149→    origin: (data.origin as string) || undefined,
150→    destination: (data.destination as string) || undefined,
151→    estimated_delivery: (data.scheduled_delivery_date as string) || undefined,
152→    checkpoints: allCheckpoints,
153→  };
154→}
