 13→async function getPublicTracking(slug: string): Promise<Parcel | null> {
 14→  const supabase = await createClient();
 15→  const { data, error } = await supabase
 16→    .from("trackings")
 17→    .select("*")
 18→    .eq("public_slug", slug)
 19→    .eq("is_public", true)
 20→    .single();
 21→
 22→  if (error || !data) return null;
 23→  return data as Parcel;
 24→}
