343→        !search ||
344→        t.tracking_number.toLowerCase().includes(search.toLowerCase()) ||
345→        t.courier_code.toLowerCase().includes(search.toLowerCase()) ||
346→        (t.user_id && emailMap[t.user_id]?.toLowerCase().includes(search.toLowerCase()));
347→      const matchStatus = statusFilter === "all" || t.status === statusFilter;
348→      return matchSearch && matchStatus;
349→    });
350→  }, [trackings, search, statusFilter, emailMap]);
351→
352→  return (
353→    <div className="space-y-4">
354→      {/* Toolbar */}
355→      <div className="flex flex-wrap items-center gap-3">
356→        <input
357→          type="text"
