  1→import { NextResponse } from "next/server";
  2→import { createClient } from "@/lib/supabase/server";
  3→
  4→export async function GET() {
  5→  const supabase = await createClient();
  6→  const {
  7→    data: { user },
  8→  } = await supabase.auth.getUser();
  9→
 10→  if (!user) {
 11→    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
 12→  }
 13→
 14→  const { data: trackings } = await supabase
 15→    .from("trackings")
 16→    .select("*")
 17→    .eq("user_id", user.id);
 18→
 19→  const all = trackings || [];
 20→  const now = new Date();
 21→  const thirtyDaysAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
 22→  const startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
 23→
 24→  // Summary stats
 25→  const total = all.length;
 26→  const inTransit = all.filter((t) => t.status === "transit").length;
 27→  const deliveredThisMonth = all.filter(
 28→    (t) =>
 29→      t.status === "delivered" && new Date(t.updated_at) >= startOfMonth
 30→  ).length;
 31→
 32→  // Average delivery time (delivered parcels with both created_at and updated_at)
 33→  const deliveredParcels = all.filter((t) => t.status === "delivered");
 34→  let avgDeliveryDays = 0;
 35→  if (deliveredParcels.length > 0) {
 36→    const totalDays = deliveredParcels.reduce((sum, t) => {
 37→      const created = new Date(t.created_at).getTime();
 38→      const updated = new Date(t.updated_at).getTime();
 39→      return sum + (updated - created) / (1000 * 60 * 60 * 24);
 40→    }, 0);
 41→    avgDeliveryDays = Math.round((totalDays / deliveredParcels.length) * 10) / 10;
 42→  }
 43→
 44→  // Tracking volume per day (last 30 days)
 45→  const volumeMap: Record<string, number> = {};
 46→  for (let i = 29; i >= 0; i--) {
 47→    const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000);
 48→    const key = date.toISOString().split("T")[0];
 49→    volumeMap[key] = 0;
 50→  }
 51→  for (const t of all) {
 52→    const key = new Date(t.created_at).toISOString().split("T")[0];
 53→    if (key in volumeMap) {
 54→      volumeMap[key]++;
 55→    }
 56→  }
 57→  const dailyVolume = Object.entries(volumeMap).map(([date, count]) => ({
 58→    date,
 59→    count,
 60→  }));
 61→
 62→  // Courier breakdown with delivery success rate
 63→  const courierMap: Record<
 64→    string,
 65→    { total: number; delivered: number }
 66→  > = {};
 67→  for (const t of all) {
 68→    if (!courierMap[t.courier_code]) {
 69→      courierMap[t.courier_code] = { total: 0, delivered: 0 };
 70→    }
 71→    courierMap[t.courier_code].total++;
 72→    if (t.status === "delivered") {
 73→      courierMap[t.courier_code].delivered++;
 74→    }
 75→  }
 76→  const courierBreakdown = Object.entries(courierMap)
 77→    .map(([courier, data]) => ({
 78→      courier,
 79→      total: data.total,
 80→      delivered: data.delivered,
 81→      successRate:
 82→        data.total > 0
 83→          ? Math.round((data.delivered / data.total) * 100)
 84→          : 0,
 85→    }))
 86→    .sort((a, b) => b.total - a.total);
 87→
 88→  // In-transit parcels
 89→  const inTransitParcels = all
 90→    .filter((t) => t.status === "transit")
 91→    .sort(
 92→      (a, b) =>
 93→        new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime()
 94→    );
 95→
 96→  return NextResponse.json({
 97→    summary: {
 98→      total,
 99→      inTransit,
100→      deliveredThisMonth,
101→      avgDeliveryDays,
102→    },
103→    dailyVolume,
104→    courierBreakdown,
105→    inTransitParcels,
106→  });
107→}
