 1→import { NextRequest, NextResponse } from "next/server";
 2→import { nanoid } from "nanoid";
 3→import { trackParcel } from "@/lib/trackingmore";
 4→import { createClient } from "@/lib/supabase/server";
 5→
 6→// Process a single tracking item from CSV import
 7→export async function POST(request: NextRequest) {
 8→  try {
 9→    const supabase = await createClient();
10→    const {
11→      data: { user },
12→    } = await supabase.auth.getUser();
13→
14→    if (!user) {
15→      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
16→    }
17→
18→    const body = await request.json();
19→    const { trackingNumber, courierCode, label } = body;
20→
21→    if (!trackingNumber || !courierCode) {
22→      return NextResponse.json(
23→        { error: "Tracking number and courier code are required" },
24→        { status: 400 }
25→      );
26→    }
27→
28→    // Call TrackingMore API
29→    const result = await trackParcel(trackingNumber, courierCode);
30→
31→    // Check if already exists
32→    const { data: existing, error: lookupError } = await supabase
33→      .from("trackings")
34→      .select("id")
35→      .eq("user_id", user.id)
36→      .eq("tracking_number", trackingNumber)
37→      .eq("courier_code", courierCode)
38→      .single();
39→
40→    if (existing && !lookupError) {
41→      const { error: updateError } = await supabase
42→        .from("trackings")
43→        .update({
44→          status: result.status,
45→          last_event: result.last_event || null,
46→          origin: result.origin || null,
47→          destination: result.destination || null,
48→          checkpoints: result.checkpoints,
49→          label: label || null,
50→        })
51→        .eq("id", existing.id);
52→      if (updateError) throw new Error(`Update failed: ${updateError.message}`);
53→    } else {
54→      const { error: insertError } = await supabase.from("trackings").insert({
55→        user_id: user.id,
56→        tracking_number: result.tracking_number,
57→        courier_code: result.courier_code,
58→        status: result.status,
59→        last_event: result.last_event || null,
60→        origin: result.origin || null,
61→        destination: result.destination || null,
62→        checkpoints: result.checkpoints,
63→        label: label || null,
64→        public_slug: nanoid(10),
65→      });
66→      if (insertError) throw new Error(`Insert failed: ${insertError.message}`);
67→    }
68→
69→    return NextResponse.json({
70→      success: true,
71→      trackingNumber,
72→      status: result.status,
73→    });
74→  } catch (error) {
75→    const message =
76→      error instanceof Error ? error.message : "Import failed";
77→    return NextResponse.json(
78→      { success: false, error: message },
79→      { status: 500 }
80→    );
81→  }
82→}
