  9→export default function ContactPage() {
 10→  const [form, setForm] = useState<ContactFormData>({
 11→    name: "",
 12→    email: "",
 13→    subject: "",
 14→    message: "",
 15→  });
 16→  const [errors, setErrors] = useState<Record<string, string>>({});
 17→  const [loading, setLoading] = useState(false);
 18→  const [submitted, setSubmitted] = useState(false);
 19→
 20→  function handleChange(
 21→    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
 22→  ) {
 23→    const { name, value } = e.target;
 24→    setForm((prev) => ({ ...prev, [name]: value }));
 25→    if (errors[name]) {
 26→      setErrors((prev) => {
 27→        const next = { ...prev };
 28→        delete next[name];
 29→        return next;
 30→      });
 31→    }
 32→  }
 33→
 34→  async function handleSubmit(e: React.FormEvent) {
 35→    e.preventDefault();
 36→    setErrors({});
 37→
 38→    // Client-side validation
 39→    const result = contactFormSchema.safeParse(form);
 40→    if (!result.success) {
 41→      const fieldErrors: Record<string, string> = {};
 42→      for (const issue of result.error.issues) {
 43→        const key = String(issue.path[0]);
 44→        if (!fieldErrors[key]) {
 45→          fieldErrors[key] = issue.message;
 46→        }
 47→      }
 48→      setErrors(fieldErrors);
 49→      return;
 50→    }
 51→
 52→    setLoading(true);
 53→
 54→    try {
 55→      const res = await fetch("/api/contact", {
 56→        method: "POST",
 57→        headers: { "Content-Type": "application/json" },
 58→        body: JSON.stringify(form),
 59→      });
 60→
 61→      const data = await res.json();
 62→
 63→      if (!res.ok) {
 64→        toast.error(data.error || "Something went wrong");
 65→        return;
 66→      }
 67→
 68→      toast.success("Message sent! We'll get back to you soon.");
 69→      setSubmitted(true);
 70→    } catch {
 71→      toast.error("Failed to send message. Please try again.");
 72→    } finally {
 73→      setLoading(false);
 74→    }
 75→  }
 76→
 77→  if (submitted) {
 78→    return (
 79→      <div className="mx-auto max-w-lg px-4 py-16 text-center">
 80→        <div className="mx-auto mb-4 flex h-16 w-16 items-center justify-center rounded-full bg-green-100 dark:bg-green-900/30">
 81→          <CheckCircle2 className="h-8 w-8 text-green-600 dark:text-green-400" />
 82→        </div>
 83→        <h1 className="text-2xl font-bold tracking-tight">
 84→          Thank you!
 85→        </h1>
 86→        <p className="mt-2 text-zinc-500 dark:text-zinc-400">
 87→          Your message has been sent. We&apos;ll get back to you as soon as
 88→          possible.
 89→        </p>
 90→        <button
 91→          onClick={() => {
 92→            setSubmitted(false);
 93→            setForm({ name: "", email: "", subject: "", message: "" });
 94→          }}
 95→          className="mt-6 rounded-lg bg-zinc-900 px-4 py-2.5 text-sm font-medium text-white hover:bg-zinc-800 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
 96→        >
 97→          Send another message
 98→        </button>
 99→      </div>
100→    );
101→  }
102→
103→  const inputClass =
104→    "mt-1 block w-full rounded-lg border border-zinc-300 px-3 py-2 text-sm shadow-sm focus:border-zinc-500 focus:outline-none focus:ring-1 focus:ring-zinc-500 dark:border-zinc-700 dark:bg-zinc-900 dark:text-white";
105→  const labelClass =
106→    "block text-sm font-medium text-zinc-700 dark:text-zinc-300";
107→
108→  return (
109→    <div className="mx-auto max-w-lg px-4 py-8">
110→      <div className="mb-8">
111→        <h1 className="text-2xl font-bold tracking-tight">Contact Us</h1>
112→        <p className="mt-1 text-sm text-zinc-500 dark:text-zinc-400">
113→          Have a question or feedback? Send us a message and we&apos;ll
114→          respond as soon as we can.
115→        </p>
116→      </div>
117→
118→      <form onSubmit={handleSubmit} className="space-y-4">
119→        <div>
120→          <label htmlFor="name" className={labelClass}>
121→            Name
122→          </label>
123→          <input
124→            id="name"
125→            name="name"
126→            type="text"
127→            value={form.name}
128→            onChange={handleChange}
129→            className={inputClass}
130→            placeholder="Your name"
131→          />
132→          {errors.name && (
133→            <p className="mt-1 text-xs text-red-500">{errors.name}</p>
134→          )}
135→        </div>
136→
137→        <div>
138→          <label htmlFor="email" className={labelClass}>
139→            Email
140→          </label>
141→          <input
142→            id="email"
143→            name="email"
144→            type="email"
145→            value={form.email}
146→            onChange={handleChange}
147→            className={inputClass}
148→            placeholder="you@example.com"
149→          />
150→          {errors.email && (
151→            <p className="mt-1 text-xs text-red-500">{errors.email}</p>
152→          )}
153→        </div>
154→
155→        <div>
156→          <label htmlFor="subject" className={labelClass}>
157→            Subject
158→          </label>
159→          <input
160→            id="subject"
161→            name="subject"
162→            type="text"
163→            value={form.subject}
164→            onChange={handleChange}
165→            className={inputClass}
166→            placeholder="What is this about?"
167→          />
168→          {errors.subject && (
169→            <p className="mt-1 text-xs text-red-500">{errors.subject}</p>
170→          )}
171→        </div>
172→
173→        <div>
174→          <label htmlFor="message" className={labelClass}>
175→            Message
176→          </label>
177→          <textarea
178→            id="message"
179→            name="message"
180→            rows={5}
181→            value={form.message}
182→            onChange={handleChange}
183→            className={inputClass + " resize-none"}
184→            placeholder="Your message (at least 10 characters)"
185→          />
186→          <div className="mt-1 flex items-center justify-between">
187→            {errors.message ? (
188→              <p className="text-xs text-red-500">{errors.message}</p>
189→            ) : (
190→              <span />
191→            )}
192→            <span className="text-xs text-zinc-400">
193→              {form.message.length}/2000
194→            </span>
195→          </div>
196→        </div>
197→
198→        <button
199→          type="submit"
200→          disabled={loading}
201→          className="inline-flex w-full items-center justify-center gap-2 rounded-full bg-zinc-900 px-4 py-2.5 text-sm font-medium text-white shadow-sm transition-all hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-zinc-900 dark:hover:bg-zinc-200"
202→        >
203→          {loading ? (
204→            <Loader2 className="h-4 w-4 animate-spin" />
205→          ) : (
206→            <Send className="h-4 w-4" />
207→          )}
208→          {loading ? "Sending..." : "Send Message"}
209→        </button>
210→      </form>
211→    </div>
212→  );
213→}
