  1→import { NextRequest, NextResponse } from "next/server";
  2→import { createClient } from "@supabase/supabase-js";
  3→import nodemailer from "nodemailer";
  4→import { contactFormSchema } from "@/lib/validators";
  5→
  6→const RATE_LIMIT_MAX = 3;
  7→const RATE_LIMIT_WINDOW_HOURS = 1;
  8→
  9→function getClientIp(request: NextRequest): string {
 10→  const forwarded = request.headers.get("x-forwarded-for");
 11→  if (forwarded) {
 12→    return forwarded.split(",")[0].trim();
 13→  }
 14→  return request.headers.get("x-real-ip") ?? "127.0.0.1";
 15→}
 16→
 17→export async function POST(request: NextRequest) {
 18→  try {
 19→    const body = await request.json();
 20→
 21→    // Server-side validation
 22→    const result = contactFormSchema.safeParse(body);
 23→    if (!result.success) {
 24→      const errors = result.error.issues.map((i) => i.message);
 25→      return NextResponse.json(
 26→        { error: "Validation failed", details: errors },
 27→        { status: 400 }
 28→      );
 29→    }
 30→
 31→    const { name, email, subject, message } = result.data;
 32→
 33→    // Use service role client to bypass RLS for rate limiting check
 34→    const supabase = createClient(
 35→      process.env.NEXT_PUBLIC_SUPABASE_URL!,
 36→      process.env.SUPABASE_SERVICE_ROLE_KEY!
 37→    );
 38→
 39→    // Rate limiting: check submissions from this IP in the last hour
 40→    const ip = getClientIp(request);
 41→    const windowStart = new Date(
 42→      Date.now() - RATE_LIMIT_WINDOW_HOURS * 60 * 60 * 1000
 43→    ).toISOString();
 44→
 45→    const { count, error: countError } = await supabase
 46→      .from("contact_submissions")
 47→      .select("*", { count: "exact", head: true })
 48→      .eq("ip_address", ip)
 49→      .gte("created_at", windowStart);
 50→
 51→    if (countError) {
 52→      console.error("Rate limit check failed:", countError);
 53→      return NextResponse.json(
 54→        { error: "Internal server error" },
 55→        { status: 500 }
 56→      );
 57→    }
 58→
 59→    if ((count ?? 0) >= RATE_LIMIT_MAX) {
 60→      return NextResponse.json(
 61→        {
 62→          error: `Too many submissions. Please try again in ${RATE_LIMIT_WINDOW_HOURS} hour.`,
 63→        },
 64→        { status: 429 }
 65→      );
 66→    }
 67→
 68→    // Store submission in database
 69→    const { error: insertError } = await supabase
 70→      .from("contact_submissions")
 71→      .insert({
 72→        name,
 73→        email,
 74→        subject,
 75→        message,
 76→        ip_address: ip,
 77→      });
 78→
 79→    if (insertError) {
 80→      console.error("Failed to save contact submission:", insertError);
 81→      return NextResponse.json(
 82→        { error: "Failed to submit your message" },
 83→        { status: 500 }
 84→      );
 85→    }
 86→
 87→    // Send email notification via SMTP (if configured)
 88→    if (
 89→      process.env.SMTP_HOST &&
 90→      process.env.SMTP_USER &&
 91→      process.env.CONTACT_EMAIL
 92→    ) {
 93→      try {
 94→        const transporter = nodemailer.createTransport({
 95→          host: process.env.SMTP_HOST,
 96→          port: Number(process.env.SMTP_PORT) || 587,
 97→          secure: Number(process.env.SMTP_PORT) === 465,
 98→          auth: {
 99→            user: process.env.SMTP_USER,
100→            pass: process.env.SMTP_PASS,
101→          },
102→        });
103→
104→        await transporter.sendMail({
105→          from: `"Parcel Tracker" <${process.env.SMTP_USER}>`,
106→          to: process.env.CONTACT_EMAIL,
107→          replyTo: email,
108→          subject: `Contact Form: ${subject}`,
109→          text: `Name: ${name}\nEmail: ${email}\nSubject: ${subject}\n\nMessage:\n${message}`,
110→          html: `
111→            <h2>New Contact Form Submission</h2>
112→            <p><strong>Name:</strong> ${escapeHtml(name)}</p>
113→            <p><strong>Email:</strong> ${escapeHtml(email)}</p>
114→            <p><strong>Subject:</strong> ${escapeHtml(subject)}</p>
115→            <hr />
116→            <p>${escapeHtml(message).replace(/\n/g, "<br />")}</p>
117→          `,
118→        });
119→      } catch (emailError) {
120→        // Log but don't fail — the submission is already saved
121→        console.error("Failed to send email notification:", emailError);
122→      }
123→    }
124→
125→    return NextResponse.json({ success: true });
126→  } catch {
127→    return NextResponse.json(
128→      { error: "Invalid request body" },
129→      { status: 400 }
130→    );
131→  }
132→}
133→
134→function escapeHtml(text: string): string {
135→  return text
136→    .replace(/&/g, "&amp;")
137→    .replace(/</g, "&lt;")
138→    .replace(/>/g, "&gt;")
139→    .replace(/"/g, "&quot;");
140→}
