 30→export function HistoryList({ initialTrackings }: HistoryListProps) {
 31→  const [trackings, setTrackings] = useState<Parcel[]>(initialTrackings);
 32→  const [statusFilter, setStatusFilter] = useState("all");
 33→  const [sortOrder, setSortOrder] = useState<SortOrder>("newest");
 34→  const [selected, setSelected] = useState<Set<string>>(new Set());
 35→  const [refreshingIds, setRefreshingIds] = useState<Set<string>>(new Set());
 36→  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
 37→  const supabase = createClient();
 38→  const { user } = useAuth();
 39→
 40→  // Realtime subscriptions — live updates across tabs/devices
 41→  useRealtimeTrackings({
 42→    userId: user?.id,
 43→    onInsert: useCallback((parcel: Parcel) => {
 44→      setTrackings((prev) => {
 45→        if (prev.some((t) => t.id === parcel.id)) return prev;
 46→        return [parcel, ...prev];
 47→      });
 48→      toast.info(`New tracking added: ${parcel.tracking_number}`);
 49→    }, []),
 50→    onUpdate: useCallback((parcel: Parcel) => {
 51→      setTrackings((prev) =>
 52→        prev.map((t) => (t.id === parcel.id ? parcel : t))
 53→      );
 54→    }, []),
 55→    onDelete: useCallback((id: string) => {
 56→      setTrackings((prev) => prev.filter((t) => t.id !== id));
 57→      setSelected((prev) => {
 58→        const next = new Set(prev);
 59→        next.delete(id);
 60→        return next;
 61→      });
 62→    }, []),
 63→  });
 64→
 65→  // Filter
 66→  const filtered = trackings.filter(
 67→    (t) => statusFilter === "all" || t.status === statusFilter
 68→  );
 69→
 70→  // Sort
 71→  const sorted = [...filtered].sort((a, b) => {
 72→    const dateA = new Date(a.updated_at).getTime();
 73→    const dateB = new Date(b.updated_at).getTime();
 74→    return sortOrder === "newest" ? dateB - dateA : dateA - dateB;
 75→  });
 76→
 77→  // Selection
 78→  const toggleSelect = (id: string) => {
 79→    setSelected((prev) => {
 80→      const next = new Set(prev);
 81→      if (next.has(id)) next.delete(id);
 82→      else next.add(id);
 83→      return next;
 84→    });
 85→  };
 86→
 87→  const toggleSelectAll = () => {
 88→    if (selected.size === sorted.length) {
 89→      setSelected(new Set());
 90→    } else {
 91→      setSelected(new Set(sorted.map((t) => t.id)));
 92→    }
 93→  };
 94→
 95→  // Refresh a single tracking
 96→  const refreshTracking = useCallback(
 97→    async (tracking: Parcel) => {
 98→      setRefreshingIds((prev) => new Set(prev).add(tracking.id));
 99→      try {
100→        const res = await fetch("/api/track", {
101→          method: "POST",
102→          headers: { "Content-Type": "application/json" },
103→          body: JSON.stringify({
104→            trackingNumber: tracking.tracking_number,
105→            courierCode: tracking.courier_code,
106→          }),
107→        });
108→        const json = await res.json();
109→        if (json.data) {
110→          // Update local state
111→          setTrackings((prev) =>
112→            prev.map((t) =>
113→              t.id === tracking.id
114→                ? {
115→                    ...t,
116→                    status: json.data.status,
117→                    last_event: json.data.last_event || t.last_event,
118→                    checkpoints: json.data.checkpoints || t.checkpoints,
119→                    updated_at: new Date().toISOString(),
120→                  }
121→                : t
122→            )
123→          );
124→          toast.success(`Updated ${tracking.tracking_number}`);
125→        } else {
126→          toast.error(json.error || "Failed to refresh");
127→        }
128→      } catch {
129→        toast.error("Failed to refresh tracking");
130→      } finally {
131→        setRefreshingIds((prev) => {
132→          const next = new Set(prev);
133→          next.delete(tracking.id);
134→          return next;
135→        });
136→      }
137→    },
138→    []
139→  );
140→
141→  // Toggle public/private
142→  const togglePublic = async (tracking: Parcel) => {
143→    const newValue = !tracking.is_public;
144→    const { error } = await supabase
145→      .from("trackings")
146→      .update({ is_public: newValue })
147→      .eq("id", tracking.id);
148→    if (error) {
149→      toast.error("Failed to update visibility");
